mber\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"page number\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"endnote reference\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"endnote text\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"table of authorities\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"macro\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"toa heading\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"List\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"List Bullet\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"List Number\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"List 2\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"List 3\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"List 4\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"List 5\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"List Bullet 2\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"List Bullet 3\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"List Bullet 4\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"List Bullet 5\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"List Number 2\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"List Number 3\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"List Number 4\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"List Number 5\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Title\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Closing\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Signature\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Default Paragraph Font\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Body Text\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Body Text Indent\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"List Continue\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"List Continue 2\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"List Continue 3\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"List Continue 4\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"List Continue 5\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Message Header\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Subtitle\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Salutation\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Date\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Body Text First Indent\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Body Text First Indent 2\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Note Heading\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Body Text 2\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Body Text 3\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Body Text Indent 2\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Body Text Indent 3\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Block Text\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Hyperlink\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"FollowedHyperlink\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Strong\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Emphasis\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Document Map\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Plain Text\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"E-mail Signature\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Normal (Web)\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"HTML Acronym\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"HTML Address\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"HTML Cite\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"HTML Code\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"HTML Definition\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"HTML Keyboard\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"HTML Preformatted\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"HTML Sample\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"HTML Typewriter\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"HTML Variable\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Normal Table\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"annotation subject\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"No List\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Balloon Text\"></w :LsdException></w :LatentStyles></xml><![endif]-->','','','','','','','','','','','','','100','65','0','1413847392');
INSERT INTO `%DB_PREFIX%info` VALUES ('683','29','一个铜币引起的财富效应','','','<!--[if gte mso 9]><xml><w :WordDocument><w :BrowserLevel>MicrosoftInternetExplorer4</w :BrowserLevel><w :DisplayHorizontalDrawingGridEvery>0</w :DisplayHorizontalDrawingGridEvery><w :DisplayVerticalDrawingGridEvery>2</w :DisplayVerticalDrawingGridEvery><w :DocumentKind>DocumentNotSpecified</w :DocumentKind><w :DrawingGridVerticalSpacing>7.8</w :DrawingGridVerticalSpacing><w :View>Normal</w :View><w :Compatibility><w :DontGrowAutofit/><w :BalanceSingleByteDoubleByteWidth/><w :DoNotExpandShiftReturn/><w :UseFELayout/></w :Compatibility><w :Zoom>0</w :Zoom></w :WordDocument></xml><![endif]--><p class=\"p0\" style=\"margin-bottom:0pt;margin-top:0pt;text-align:left;\"><span style=\"mso-spacerun:\'yes\';font-weight:normal;font-size:14.0000pt;font-family:\'宋体\';\">&nbsp;&nbsp;&nbsp; <b>小故事告诉你当代人应具备的管理素质</b><br />\r\n</span></p>\r\n<p class=\"p0\" style=\"margin-bottom:0pt;margin-top:0pt;text-align:left;\"><span style=\"mso-spacerun:\'yes\';font-weight:normal;font-size:14.0000pt;font-family:\'宋体\';\">&nbsp;&nbsp; <br />\r\n</span></p>\r\n<p class=\"p0\" style=\"margin-bottom:0pt;margin-top:0pt;text-align:left;\"><span style=\"mso-spacerun:\'yes\';font-weight:normal;font-size:14.0000pt;font-family:\'宋体\';\">&nbsp;&nbsp;&nbsp; 有个年轻人，抓了一只老鼠，卖给药铺，他得到了一枚铜币。他走过花园，听花匠们说口渴，他又有了想法。他用这枚铜币买了一点糖浆，和着水送给花匠们喝。花匠们喝了水，便一人送他一束花。他到集市卖掉这些花，得到了八个铜币。</span></p>\r\n<p class=\"p0\" style=\"margin-bottom:0pt;margin-top:0pt;text-align:left;\"><span style=\"mso-spacerun:\'yes\';font-weight:normal;font-size:14.0000pt;font-family:\'宋体\';\">&nbsp;&nbsp;&nbsp;&nbsp;一天，风雨交加，果园里到处都是被狂风吹落的枯枝败叶。年轻人对园丁说：“如果这些断枝落叶送给我，我愿意把果园打扫干净。”园丁很高兴：“可以，你都拿去吧！”年轻人用八个铜币买了一些糖果，分给一群玩耍的小孩，小孩们帮他把所有的残枝败叶捡拾一空。年轻人又去找皇家厨工说有一堆柴想卖给他们，厨工付了16个铜币买走了这堆柴火。</span></p>\r\n<p class=\"p0\" style=\"margin-bottom:0pt;margin-top:0pt;text-align:left;\"><span style=\"mso-spacerun:\'yes\';font-weight:normal;font-size:14.0000pt;font-family:\'宋体\';\">&nbsp;&nbsp;&nbsp;&nbsp;年轻人用16个铜币谋起了生计，他在离城不远的地方摆了个茶水摊，因为附近有500个割草工人要喝水。不久，他认识了一个路过喝水的商人，商人告诉他：“明天有个马贩子带400匹马进城。”听了商人的话，年轻人想了一会，对割草工人说：“今天我不收钱了，请你们每人给我一捆草，行吗？”工人们很慷慨地说：“行啊！”这样，年轻人有了500捆草。第二天，马贩子来了要买饲料，便出了1000个铜币买下了年轻人的500捆草。</span></p>\r\n<p class=\"p0\" style=\"margin-bottom:0pt;margin-top:0pt;text-align:left;\"><span style=\"mso-spacerun:\'yes\';font-weight:normal;font-size:14.0000pt;font-family:\'宋体\';\">&nbsp;&nbsp;&nbsp;&nbsp;几年后，年轻人成了远近闻名的大财主故事很简单，也很有意思。年轻人的成功不是偶然的，因为他具备了现代人的管理素质。</span></p>\r\n<p class=\"p0\" style=\"margin-bottom:0pt;margin-top:0pt;text-align:left;\"><span style=\"mso-spacerun:\'yes\';font-weight:normal;font-size:14.0000pt;font-family:\'宋体\';\">一、他很有思想：他明白要想得到就一定要付出。他先送水给花匠喝，花匠得到了好处，便给了他回报。这也是双赢的智慧。</span></p>\r\n<p class=\"p0\" style=\"margin-bottom:0pt;margin-top:0pt;text-align:left;\"><span style=\"mso-spacerun:\'yes\';font-weight:normal;font-size:14.0000pt;font-family:\'宋体\';\">二、他很有眼光：他知道那些断枝落叶可以卖个好价钱，但如何得到大有学问。所以，他提出以劳动换取。这也符合勤劳制富的社会准则。</span></p>\r\n<p class=\"p0\" style=\"margin-bottom:0pt;margin-top:0pt;text-align:left;\"><span style=\"mso-spacerun:\'yes\';font-weight:normal;font-size:14.0000pt;font-family:\'宋体\';\">三、他很有组织能力：他知道，单靠他一个人难以完成这项工作。他组织了一帮小孩为他工作，并用糖果来支付报酬。从这一点看，他具备领导艺术和管理才能，他用较低的成本赢得了较大的投资收益。</span></p>\r\n<p class=\"p0\" style=\"margin-bottom:0pt;margin-top:0pt;text-align:left;\"><span style=\"mso-spacerun:\'yes\';font-weight:normal;font-size:14.0000pt;font-family:\'宋体\';\">四、他很有信息意识：他可以从和商人的谈话中捕捉赚钱的机会，再用较低的价格收购了一大批草转手卖了个好价钱。这一点，与我们今天在信息时代的经济贸易非常吻合。</span></p>\r\n<p class=\"p0\" style=\"margin-bottom:0pt;margin-top:0pt;text-align:left;\"><span style=\"mso-spacerun:\'yes\';font-weight:normal;font-size:14.0000pt;font-family:\'宋体\';\">&nbsp;&nbsp;&nbsp; <b>致富感想</b><br />\r\n</span></p>\r\n<p class=\"p0\" style=\"margin-bottom:0pt;margin-top:0pt;text-align:left;\"><span style=\"mso-spacerun:\'yes\';font-weight:normal;font-size:14.0000pt;font-family:\'宋体\';\">&nbsp;&nbsp;&nbsp; <br />\r\n</span></p>\r\n<p class=\"p0\" style=\"margin-bottom:0pt;margin-top:0pt;text-align:left;\"><span style=\"mso-spacerun:\'yes\';font-weight:normal;font-size:14.0000pt;font-family:\'宋体\';\">&nbsp;&nbsp;&nbsp; 我们每个人都梦想成功，而且财富就在我们身边。有的人抱怨财运不佳，有的人埋怨社会不公，有的人感慨父母无能……其实我们真正缺乏的正是勤奋和发现财富的慧眼。</span></p>\r\n<p class=\"p0\" style=\"margin-bottom:0pt;margin-top:0pt;text-align:left;\"><span style=\"mso-spacerun:\'yes\';font-weight:bold;font-size:14.0000pt;font-family:\'宋体\';\">&nbsp;&nbsp;&nbsp;</span><span style=\"mso-spacerun:\'yes\';font-weight:normal;font-size:14.0000pt;font-family:\'宋体\';\">你可以不会挣大钱，但是你必须学会理财。文中的年轻人，他的成功除了拥有商人的智慧，它更懂得财富积累，掌握时机。而当今时代，互联网金融新兴P2P理财，不乏是当代无门槛的不可多得的商机。你的财富需要积累，掌握当前商机，轻松攒下百万财富。</span></p>\r\n<!--[if gte mso 9]><xml><w :LatentStyles deflockedstate=\"false\" defunhidewhenused=\"true\" defsemihidden=\"true\" defqformat=\"false\" defpriority=\"99\" latentstylecount=\"156\"><w :LsdException locked=\"false\" priority=\"99\" name=\"Normal\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"heading 1\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"heading 2\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"heading 3\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"heading 4\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"heading 5\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"heading 6\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"heading 7\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"heading 8\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"heading 9\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"index 1\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"index 2\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"index 3\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"index 4\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"index 5\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"index 6\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"index 7\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"index 8\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"index 9\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"toc 1\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"toc 2\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"toc 3\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"toc 4\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"toc 5\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"toc 6\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"toc 7\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"toc 8\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"toc 9\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"Normal Indent\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"footnote text\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"annotation text\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"header\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"footer\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"index heading\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"caption\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"table of figures\"></w :LsdException><w :LsdException locked=\"false\" priority=\"99\" name=\"envelope address\"></w