DROP TABLE IF EXISTS `%DB_PREFIX%feed_back`;
CREATE TABLE `%DB_PREFIX%feed_back` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `uid` bigint(20) DEFAULT '0' COMMENT '会员ID 关联%DB_PREFIX%web_user表主键',
  `score` int(11) DEFAULT '0' COMMENT '意见奖励积分',
  `is_check` int(1) DEFAULT '0' COMMENT '审核状态： 0未采纳 1 已采纳',
  `is_read` int(1) DEFAULT '0' COMMENT '阅读状态 0 未读 1 已读',
  `remark` text COMMENT '管理员回复',
