  `create_time` int(11) DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=154 DEFAULT CHARSET=utf8 COMMENT='意见反馈表';
DROP TABLE IF EXISTS `%DB_PREFIX%info`;
CREATE TABLE `%DB_PREFIX%info` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `pid` int(11) NOT NULL COMMENT '类别id',
  `title` varchar(200) NOT NULL COMMENT '标题',
  `keyword` varchar(200) DEFAULT NULL COMMENT '关键字',
  `description` varchar(400) DEFAULT NULL COMMENT '描述',
  `content` text COMMENT '内容',
  `img` varchar(400) DEFAULT NULL COMMENT '单图',
  `imgs` varchar(4000) DEFAULT NULL COMMENT '图片组',
  `company_action` varchar(4000) DEFAULT NULL COMMENT '企业实景（图片组 仅用于公司简介id26）',
  `team_gather` varchar(4000) DEFAULT NULL COMMENT '团队集锦（图片组 仅用于公司简介id26）',
  `reg_fund` varchar(50) DEFAULT NULL COMMENT '注册资金',
  `company_create_time` varchar(20) DEFAULT NULL COMMENT '公司成立时间',
  `join_time` varchar(20) DEFAULT NULL COMMENT '开始合作时间',
  `company_url` varchar(50) DEFAULT NULL COMMENT '公司网址',
  `company_tel` varchar(20) DEFAULT NULL COMMENT '联系电话',
  `company_address` varchar(150) DEFAULT NULL COMMENT '公司地址',
  `url` varchar(400) DEFAULT NULL COMMENT '链接地址',
  `tel` varchar(20) DEFAULT NULL COMMENT '一键拨号',
  `sort` int(11) NOT NULL DEFAULT '100' COMMENT '排序',
  `click` int(11) NOT NULL DEFAULT '0' COMMENT '点击次数',
  `del` tinyint(1) NOT NULL DEFAULT '0' COMMENT '伪删除',
  `time` int(11) NOT NULL COMMENT '发布时间',
  PRIMARY KEY (`id`),
  KEY `pid` (`pid`,`title`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=1644 DEFAULT CHARSET=utf8 COMMENT='信息中心';
