INSERT INTO `%DB_PREFIX%msg_box` VALUES ('318','添加信息成功','【后台用户：admin】添加信息成功。','1','1415344475','2','2','INSERT INTO `tp_info` (`pid`,`title`,`img`,`imgs`,`url`,`tel`,`sort`,`click`,`time`,`keyword`,`description`,`content`) VALUES (42,\' 吉林省华兴泰丰信用担保有限公司\',\'/Uploads/201411/545c70d283aef.jpg\',\'/Uploads/201411/545c715655e83.jpg\\r\\n/Uploads/201411/545c7156783d4.jpg\\r\\n/Uploads/201411/545c71569a925.jpg\\r\\n/Uploads/201411/545c7156e6dd9.jpg\',\'\',\'\',100,100,1415344220,\'\',\'吉林省华兴泰丰信用担保有限公司由中电华兴能源投资控股集团全资控股。公司行业及行政各项许可齐备，经营管理制度健全，管理团队优秀。公司组建了金融从业经验丰富的经理团队，设行政部、财务部、风险管理部及担保业务等部共6个部门。\',\'<p>&nbsp;&nbsp;&nbsp;&nbsp;吉林省华兴泰丰信用担保有限公司由中电华兴能源投资控股集团全资\\r\\n\\r\\n控股，注册资本金2亿元人民币，办公地址位于建设街333号（近北安路）。</p><p>公司办公面积1000余平米，行业及行政各项许可齐备，经营管理制度\\r\\n\\r\\n健全，管理团队优秀。公司组建了金融从业经验丰富的经理团队，设行政部、财务部、风险管\\r\\n\\r\\n理部及担保业务一、二、三部共6个部门。公司法定代表人及高管人员均从事金融、担保经营\\r\\n\\r\\n管理工作多年，有丰富的金融从业经验和专业知识。公司员工共计四十余人，均具有本科以上\\r\\n\\r\\n学历并多年从业于银行、担保、小贷、财会等行业。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;公司秉承&quot;利信义道，稳健运行&quot;的核心价值观，践行&quot;客户至上，客户\\r\\n\\r\\n是价值创造源泉&quot;的服务理念，坚持双赢和利益共享，竭力为中小微企业及个人提供方便、快\\r\\n\\r\\n捷、及时、配套的专业担保服务，愿社会各界朋友前来洽谈交流，与我们携手同行，共创美好\\r\\n\\r\\n未来。</p><p><br/></p>\')','0');
