INSERT INTO `%DB_PREFIX%msg_box` VALUES ('340','添加信息成功','【后台用户：admin】添加信息成功。','1','1415598022','2','2','INSERT INTO `tp_info` (`pid`,`title`,`img`,`imgs`,`url`,`tel`,`sort`,`click`,`time`,`keyword`,`description`,`content`) VALUES (42,\'国付宝\',\'\',\'\',\'\',\'\',100,100,1415597959,\'\',\'国付宝信息科技有限公司是依托中国国际电子商务中心发起组建的，针对政府及企业的需求和电子商务的发展，精心打造的国有背景独立第三方支付平台。\',\'<p>国付宝信息科技有限公司是依托中国国际电子商务中心发起组建的，针对政府及企业的需求和电子商务的发展，精心打造的国有背景独立第三方支付平台。</p><p>国付宝深入了解政府及中小企业的支付需求，依托国有背景和先进的支付技术，创新的服务理念，经过精心研发，推出了&quot;国付宝&quot;系列电子支付产品，为用户提供完整的在线支付解决方案。&quot;国付宝&quot;拥有雄厚的资金实力，由支付行业资深管理团队经营，为用户提供7× 24小时的技术支持与服务。</p><p>公司拥有技术强大、实力雄厚的系统开发团队，国家级实力合作背景，以及卓越的市场开拓团队，为国付宝立志于做&quot;中国最大的独立第三方支付平台&quot;的宏伟目标打下坚实基础。</p><p><br/></p>\')','0');
