INSERT INTO `%DB_PREFIX%msg_box` VALUES ('363','添加信息成功','【后台用户：admin】添加信息成功。','1','1415760237','2','2','INSERT INTO `tp_info` (`pid`,`title`,`img`,`imgs`,`url`,`tel`,`sort`,`click`,`time`,`keyword`,`description`,`content`) VALUES (42,\'吉林省恺丰典当有限公司\',\'/Uploads/201411/5462c92017683.jpg\',\'/Uploads/201411/5462c93a2e4b9.jpg\\r\\n/Uploads/201411/5462c93a4cd01.jpg\\r\\n/Uploads/201411/5462c93a63b37.jpg\',\'\',\'\',100,100,1415760070,\'\',\'​在公司成立进程中，恺丰典当的经营规模从小到大，典当经营品类由少到多，典当融资能力由弱到强，在帮助市民消费理财、推动个人创业投资、解决民营企业资金急需等方面发挥了典当特有的积极作用。\',\'<p>在公司成立进程中，恺丰典当的经营规模从小到大，典当经营品类由少到多，典当融资能力由弱到强，在帮助市民消费理财、推动个人创业投资、解决民营企业资金急需等方面发挥了典当特有的积极作用。</p><p>在公司经营过程中，恺丰典当从练好内功、提高自身、发展优势上下功夫，通过优质的服务、完善的经营、通畅的运作，高效的决策，不断完善提升企业素质。</p><p><br/></p>\')','0');
