INSERT INTO `%DB_PREFIX%msg_box` VALUES ('673','修改信息成功','【后台用户：admin】修改信息成功。','1','1418347259','2','2','UPDATE `tp_info` SET `pid`=41,`title`=\'平台原理\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=101,`time`=1414134716,`keyword`=\'平台原理\',`description`=\'平台原理\',`content`=\'<p style=\\\"margin: 0px; padding: 0px; border: none; list-style-type: none; line-height: 50px; font-size: 18px; color: rgb(65, 65, 65); font-weight: normal;\\\"><img alt=\\\"fk_img.jpg\\\" src=\\\"/Uploads/201411/545c6644c0b7f.jpg\\\" title=\\\"\\\" height=\\\"418\\\" width=\\\"768\\\"/></p><h3 style=\\\"margin: 0px; padding: 0px; border: none; list-style-type: none; height: 50px; line-height: 50px; font-size: 18px; color: rgb(65, 65, 65); font-weight: normal;\\\">主要流程</h3><p><span style=\\\"color:#ff9900;margin: 0px; padding: 0px;\\\">【步骤一】</span>&nbsp;借贷企业准备好资料，向平台提出借款申请。</p><p><span style=\\\"color:#ff9900;margin: 0px; padding: 0px;\\\">【步骤二】</span>&nbsp;平台对其进行严格的信用审核和风险评估，然后将优质的借贷企业推荐给投资人。</p><p><span style=\\\"color:#ff9900;margin: 0px; padding: 0px;\\\">【步骤三】</span>&nbsp;投资人对推荐的优质借贷企业进行筛选，选出符合其要求的借贷企业，将资金通过第三方支付平台以分散出借的形式借给借贷企业。</p><p><span style=\\\"color:#ff9900;margin: 0px; padding: 0px;\\\">【步骤四】</span>&nbsp;借贷企业根据合同约定，以分期还款的形式归还投资人本息。</p><p><span style=\\\"color:#ff9900;margin: 0px; padding: 0px;\\\">【步骤五】</span>&nbsp;借贷人归还的利息，就是投资人的收益。</p><h3 style=\\\"margin: 0px; padding: 0px; border: none; list-style-type: none; height: 50px; line-height: 50px; font-size: 18px; color: rgb(65, 65, 65); font-weight: normal;\\\">金投资平台机制</h3><p style=\\\"margin-top: 0px; margin-bottom: 0px; padding: 0px; border: none; list-style-type: none; font-size: 14px; color: rgb(129, 129, 129); line-height: 38px;\\\">金投资为有资金需求的企业和理财需求的个人搭建了一个公平、透明、稳定、高效的网络互动平台，是一个提供中介服务的网络金融平台。用户可以在金投资上获得信用评级、发布借款请求满足企业的资金需要；也可以把自己的闲余资金通过金投资出借给信用良好有资金需求的企业，在获得良好的资金回报率的同时帮助了他人。</p><p><br/></p>\' WHERE ( id = 76 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('674','新用户注册','【前台会员：18943608080】注册成功','122','1418352657','1','1','INSERT INTO `tp_web_user` (`pid`,`user_name`,`phone`,`user_pwd`,`email`,`referer`,`code_number`,`is_effect`,`create_time`,`bonus_money`) VALUES (0,\'18943608080\',\'18943608080\',\'99e52a964ae71f2ffb31d98982d402f1\',\'20650344@qq.com\',null,6431,0,1418352657,50)','0');
