INSERT INTO `%DB_PREFIX%msg_box` VALUES ('915','修改项目成功','【后台用户：admin】修改【项目信息：装饰材料公司经营贷】成功。','1','1419820619','2','2','UPDATE `tp_loan` SET `loan_title`=\'装饰材料公司经营贷\',`recommend`=1,`loan_money`=2400000,`min_loan_money`=100,`max_loan_money`=200000,`loan_rate`=8,`loan_deadline`=1,`end_time`=7,`repayment_mode`=\'利随本清\',`guarantee_mode`=\'足值抵押\',`guarantee_purview`=\'本金及利息\',`loan_purpose`=\'生产经营\',`pawn`=\'设备\',`production_value`=\'700万\',`payment_source`=\'应收账款\',`repayment_ability`=\'优\',`loan_description`=\'<p>借款公司从事开发、制造和销售新型装饰材料。公司目前拥有多台高精密生产设备和具有一批较强专业水平的技术人才。</p><p>公司主营各种装饰材料，广泛适用于家装、高级写字楼、办公室、走廊、车站、厂房、医院、会所、银行、商场、展览馆等建筑室内空间。</p><p>该借款公司近年来发展迅速，产品大量展示于各大型建材市场、经销展厅，销量增长迅速，销量有保证，资金回笼速度快。</p><p><br/></p><h3class=\\\"jkf_h\\\"style=\\\"margin:padding:=\\\"\\\"color:=\\\"\\\"font-family:=\\\"\\\"white-space:=\\\"\\\"border:=\\\"\\\"list-style-type:=\\\"\\\"font-size:=\\\"\\\"line-height:=\\\"\\\"background-color:=\\\"\\\"h3class=\\\"jkf_h\\\"style=\\\"&quot;margin:\\\"></h3class=\\\"jkf_h\\\"style=\\\"margin:>\',`company_name`=\'荣御装饰材料\',`company_info`=\'\',`contract_agreement`=\'\',`field_visit`=\'/Uploads/201412/549781a1c78df.jpg\',`guarantee_title`=\'\',`guarantee_measure`=\'\',`loan_order`=10,`deal_status`=4,`start_time`=1419213600,`bad_time`=0,`bad_msg`=\'\',`success_time`=1419814749,`repay_confirm_time`=1419814800,`is_effect`=1,`update_time`=1419820619,`update_by`=\'admin\' WHERE ( id = 15 )','0');
