INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1072','修改信息成功','【后台用户：admin】修改信息成功。','1','1420697965','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'BC部-后端运营\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=101,`time`=1411020396,`keyword`=\'BC部-后端运营\',`description`=\'BC部-后端运营\',`content`=\'<p><strong>所属部门</strong>：BC部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; &nbsp;<strong>工作性质：</strong>全职</p><p><strong>岗位职责：</strong></p><p>1、负责对已有的客户资源进行专业的管理和维护，与客户建立良好的关系。 &nbsp; &nbsp;</p><p>2、重点客户拓展再营销 &nbsp; &nbsp;</p><p>3、搜集市场信息 &nbsp; &nbsp;</p><p>4、 具备对各类客户采用不策略和工具的判断能力，确保充值金额和反馈率的最大化</p><p><strong>任职要求：</strong></p><p>1、大专及以上学历，营销、管理、金融等相关专业优先；</p><p>2、形象良好，具有良好的客户沟通、人际交往及维系客户关系的能力；</p><p>3、诚实守信，品行良好；能够有效开发客户资源；</p><p>4、勤奋努力，具有吃苦耐劳以达成工作目标的毅力和决心。</p><p><br/></p>\' WHERE ( id = 71 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1073','修改信息成功','【后台用户：admin】修改信息成功。','1','1420698073','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'BC部-下贷款推广\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1414992896,`keyword`=\'BC部-下贷款推广\',`description`=\'BC部-下贷款推广\',`content`=\'<p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">所属部门：</span></strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:Calibri\\\">BC</span><span style=\\\"font-family:宋体\\\">部&nbsp;</span><span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:Calibri\\\">&nbsp;</span><span style=\\\"font-family:宋体\\\">工作地点：</span></strong><span style=\\\"font-family:宋体\\\">长春</span><span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作性质：</span></strong><span style=\\\"font-family:宋体\\\">全职</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">岗位职责：</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1<span style=\\\"font-family:宋体\\\">、负责开拓贷款客户渠道，寻找目标客户群；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2<span style=\\\"font-family:宋体\\\">、根据公司要求完成销售业绩。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3<span style=\\\"font-family:宋体\\\">、为客户提供贷款的种类、期限、利率、条件和所需的材料咨询服务。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">4<span style=\\\"font-family:宋体\\\">、指导客户填写贷款申请、各种表格等资料，并负责受理借款申请和审核所需的材料；严格审核借款人的真实性、手续的规范性、贷款使用的准确性。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">5<span style=\\\"font-family:宋体\\\">、到户到企业调查，主要对借款人的资信等级以及借款的合法性、真实性、营利性等情况进行调查，核实抵押物、质押物、保证人情况。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">6<span style=\\\"font-family:宋体\\\">、努力完成本职工作，按照规范化、制度化、程序化的要求，做好贷款客户资料的整理、立卷归档工作。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">7<span style=\\\"font-family:宋体\\\">、遵循贷款市场要求和营销规则，提高经营效益。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">8<span style=\\\"font-family:宋体\\\">、完成领导交办的其他事项。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">9<span style=\\\"font-family:宋体\\\">、按照公司制度明确落实完成工作。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">任职要求：</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1<span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">1-2</span><span style=\\\"font-family:宋体\\\">年以上销售行业工作经验，业绩突出者优先；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2<span style=\\\"font-family:宋体\\\">、反应敏捷、表达能力强，具有较强的沟通能力及交际技巧，具有亲和力；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3<span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">.</span><span style=\\\"font-family:宋体\\\">具备一定的市场分析及判断能力，良好的客户服务意识；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">4<span style=\\\"font-family:宋体\\\">、有责任心，能承受较大的工作压力；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">5<span style=\\\"font-family:宋体\\\">、有团队协作精神，善于挑战。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">6<span style=\\\"font-family:宋体\\\">、大专以上学历，有经验者优先</span></span></p><p><br/></p>\' WHERE ( id = 79 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1074','修改信息成功','【后台用户：admin】修改信息成功。','1','1420698128','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'编辑部-理财数据运营专员\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1414992929,`keyword`=\'编辑部-理财数据运营专员\',`description`=\'编辑部-理财数据运营专员\',`content`=\'<p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">所属部门：</span></strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">编辑部&nbsp;<span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:Calibri\\\">&nbsp;</span><span style=\\\"font-family:宋体\\\">工作地点：</span></strong><span style=\\\"font-family:宋体\\\">长春&nbsp;</span><span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作性质：</span></strong><span style=\\\"font-family:宋体\\\">全职</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">岗位职责：</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1<span style=\\\"font-family:宋体\\\">、负责融</span><span style=\\\"font-family:Calibri\\\">360</span><span style=\\\"font-family:宋体\\\">理财频道网贷投资、互联网理财、银行理财产品的数据搜集、整理；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2<span style=\\\"font-family:宋体\\\">、对融</span><span style=\\\"font-family:Calibri\\\">360</span><span style=\\\"font-family:宋体\\\">理财平台数据库进行分析，提炼出对用户、机构有价值的数据信息。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">任职要求：</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1<span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">1</span><span style=\\\"font-family:宋体\\\">年以上理财相关行业工作经历（第三方理财、理财编辑等）；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2<span style=\\\"font-family:宋体\\\">、了解网贷投资、互联网理财、银行理财；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3<span style=\\\"font-family:宋体\\\">、有较好的沟通能力，有过数据整理和分析经验；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">4<span style=\\\"font-family:宋体\\\">、本科以上学历。</span></span></p><p><br/></p>\' WHERE ( id = 80 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1075','修改信息成功','【后台用户：admin】修改信息成功。','1','1420698174','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'编辑部-金融产品分析师\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1419474731,`keyword`=\'编辑部-金融产品分析师\',`description`=\'编辑部-金融产品分析师\',`content`=\'<p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">所属部门：</span></strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">编辑部&nbsp;<span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作地点：</span></strong><span style=\\\"font-family:宋体\\\">长春</span><span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作性质：</span></strong><span style=\\\"font-family:宋体\\\">全职</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">岗位职责：</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1<span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span><span style=\\\"font-family:宋体\\\">负责内容运营部专业报告的框架搭建及报告撰写工作；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2<span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span><span style=\\\"font-family:宋体\\\">依据报告要求完成数据采集的数据要求及规范工作，并负责采集到数据的价值挖掘工作。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">任职要求：</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1<span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span><span style=\\\"font-family:宋体\\\">金融或统计相关专业本科以上学历；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2<span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span><span style=\\\"font-family:宋体\\\">有至少五年以上的专业报告撰写经验；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3<span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span><span style=\\\"font-family:宋体\\\">有至少五年以上的金融产品数据分析经验；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">4<span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span><span style=\\\"font-family:宋体\\\">有严谨的工作态度，对数据具有高度敏感性。</span></span></p><p><br/></p>\' WHERE ( id = 964 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1076','添加信息成功','【后台用户：admin】添加信息成功。','1','1420698215','2','2','INSERT INTO `tp_info` (`pid`,`title`,`img`,`imgs`,`url`,`tel`,`sort`,`click`,`time`,`keyword`,`description`,`content`) VALUES (34,\'风控部-贷款专员\',\'\',\'\',\'\',\'\',100,100,1420698186,\'风控部-贷款专员\',\'风控部-贷款专员\',\'<p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">所属部门：</span></strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">风控部&nbsp;<span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作地点：</span></strong><span style=\\\"font-family:宋体\\\">长春&nbsp;</span><span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作性质：</span></strong><span style=\\\"font-family:宋体\\\">全职</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">岗位职责</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">&nbsp;1<span style=\\\"font-family:宋体\\\">、支持融</span><span style=\\\"font-family:Calibri\\\">360</span><span style=\\\"font-family:宋体\\\">的风控业务；在网点与贷款客户沟通并访谈；检查与核算客户资料，并进行资料录入；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2<span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span><span style=\\\"font-family:宋体\\\">偶尔到客户地点进行调研；无销售业绩指标。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">任职要求</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1<span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span><span style=\\\"font-family:宋体\\\">大专以上学历；良好的沟通能力和人际关系能力；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2<span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span><span style=\\\"font-family:宋体\\\">基本电脑知识（含</span><span style=\\\"font-family:Calibri\\\">office</span><span style=\\\"font-family:宋体\\\">）；有客户服务经验（零售、中介等）更佳；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3<span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span><span style=\\\"font-family:宋体\\\">良好的学习能力，团队意识强。</span></span></p><p><br/></p>\')','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1077','添加信息成功','【后台用户：admin】添加信息成功。','1','1420698258','2','2','INSERT INTO `tp_info` (`pid`,`title`,`img`,`imgs`,`url`,`tel`,`sort`,`click`,`time`,`keyword`,`description`,`content`) VALUES (34,\'风控部-算法和机器学习架构师\',\'\',\'\',\'\',\'\',100,100,1420698224,\'风控部-算法和机器学习架构师\',\'风控部-算法和机器学习架构师\',\'<p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">所属部门：</span></strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">风控部&nbsp;<span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作地点：</span></strong><span style=\\\"font-family:宋体\\\">长春&nbsp;</span><span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作性质：</span></strong><span style=\\\"font-family:宋体\\\">全职</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">岗位职责：</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1.<span style=\\\"font-family:宋体\\\">从个人征信数据、行为数据、互联网数据等出发，构建大数据金融模型用于贷款自动化授信，以及相关数据和模型建设</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2.<span style=\\\"font-family:宋体\\\">带领完成从模型构建到工程实现的整体设计、实现工作</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3.<span style=\\\"font-family:宋体\\\">协助规划构建相关技术线团队</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">任职要求</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1.<span style=\\\"font-family:宋体\\\">很熟练的掌握统计机器学习方向技能（如：决策树、逻辑回归、神经网络、图模型，概率统计，最优化方法等），有良好的建模能力。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2.<span style=\\\"font-family:宋体\\\">熟悉互联网数据挖掘经验者优先（如数据抓取、文本挖掘、分类、</span><span style=\\\"font-family:Calibri\\\">NLP</span><span style=\\\"font-family:宋体\\\">方向）</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3.<span style=\\\"font-family:宋体\\\">统计、数学、运筹学或计算机数据挖掘</span><span style=\\\"font-family:Calibri\\\">/</span><span style=\\\"font-family:宋体\\\">机器学习方向</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span><span style=\\\"font-family:宋体\\\">硕士毕业</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">4.<span style=\\\"font-family:宋体\\\">有良好的工程实现能力，有</span><span style=\\\"font-family:Calibri\\\">2</span><span style=\\\"font-family:宋体\\\">年以上代码工程实践经验，有多个产品在互联网平台上线经验</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">5.<span style=\\\"font-family:宋体\\\">熟悉一门或几门以下技术</span><span style=\\\"font-family:Calibri\\\">:C/C++,Linux/Unix&nbsp;Shell,Python,PHP</span><span style=\\\"font-family:宋体\\\">编程。</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span><span style=\\\"font-family:宋体\\\">会用</span><span style=\\\"font-family:Calibri\\\">SAS</span><span style=\\\"font-family:宋体\\\">或</span><span style=\\\"font-family:Calibri\\\">SPSS</span><span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">R</span><span style=\\\"font-family:宋体\\\">语言。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">6.<span style=\\\"font-family:宋体\\\">互联网公司工作经验不低于</span><span style=\\\"font-family:Calibri\\\">3</span><span style=\\\"font-family:宋体\\\">年</span></span></p><p><br/></p>\')','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1078','添加信息成功','【后台用户：admin】添加信息成功。','1','1420698292','2','2','INSERT INTO `tp_info` (`pid`,`title`,`img`,`imgs`,`url`,`tel`,`sort`,`click`,`time`,`keyword`,`description`,`content`) VALUES (34,\'风控部-复审\',\'\',\'\',\'\',\'\',100,100,1420698268,\'风控部-复审\',\'风控部-复审\',\'<p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">所属部门：</span></strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">风控部&nbsp;<span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作地点：</span></strong><span style=\\\"font-family:宋体\\\">长春&nbsp;</span><span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作性质：</span></strong><span style=\\\"font-family:宋体\\\">全职</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">岗位职责：</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1<span style=\\\"font-family:宋体\\\">、审核借款申请人资料的真实性、完整性；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2<span style=\\\"font-family:宋体\\\">、对借款申请人各项资料进行复合，形成终审意见；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3<span style=\\\"font-family:宋体\\\">、对初审给予指导意见；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">4<span style=\\\"font-family:宋体\\\">、与其他部门同事合作，完善审核流程，加强风险管理，降低风险及信贷损失。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">5<span style=\\\"font-family:宋体\\\">、在工作中积极提出自己的意见和建议，优化工作流程。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">6<span style=\\\"font-family:宋体\\\">、领导交予的其他工作</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">任职要求：</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1.&nbsp;<span style=\\\"font-family:宋体\\\">大学本科及以上学历</span><span style=\\\"font-family:Calibri\\\">,</span><span style=\\\"font-family:宋体\\\">金融、法律、财会类或经济类等相关专业毕业优先；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2.&nbsp;<span style=\\\"font-family:宋体\\\">具备</span><span style=\\\"font-family:Calibri\\\">3-5</span><span style=\\\"font-family:宋体\\\">年以上同行业信贷审查相关知识技能、授信审查审批、风险管理等工作经验者优先；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3.&nbsp;<span style=\\\"font-family:宋体\\\">熟悉信贷业务及相关法规、制度，具有极强的分析能力及风险防范能力，有良好的职业操守，无不良工作记录；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">4<span style=\\\"font-family:宋体\\\">、工作主动负责，认真仔细，勤勉敬业，善于沟通，有较强的客户服务意识；</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span><span style=\\\"font-family:宋体\\\">具有良好的客户沟通、人际交往及维系客户关系的能力。</span></span></p><p><br/></p>\')','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1079','添加信息成功','【后台用户：admin】添加信息成功。','1','1420698329','2','2','INSERT INTO `tp_info` (`pid`,`title`,`img`,`imgs`,`url`,`tel`,`sort`,`click`,`time`,`keyword`,`description`,`content`) VALUES (34,\'风控部-审核专员\',\'\',\'\',\'\',\'\',100,100,1420698299,\'风控部-审核专员\',\'风控部-审核专员\',\'<p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">所属部门：</span></strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">风控部&nbsp;<span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:Calibri\\\">&nbsp;</span><span style=\\\"font-family:宋体\\\">工作地点：</span></strong><span style=\\\"font-family:宋体\\\">长春&nbsp;</span><span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作性质：</span></strong><span style=\\\"font-family:宋体\\\">全职</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">岗位职责：</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1<span style=\\\"font-family:宋体\\\">、审核借款申请人资料的真实性、完整性；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2<span style=\\\"font-family:宋体\\\">、按要求对借款申请人进行调查，审核借款人的资信条件，形成初步审核意见；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3<span style=\\\"font-family:宋体\\\">、与其他部门同事合作，完善审核流程，加强风险管理，降低风险及信贷损失。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">4<span style=\\\"font-family:宋体\\\">、在工作中积极提出自己的意见和建议，优化工作流程。