INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1080','添加信息成功','【后台用户：admin】添加信息成功。','1','1420698365','2','2','INSERT INTO `tp_info` (`pid`,`title`,`img`,`imgs`,`url`,`tel`,`sort`,`click`,`time`,`keyword`,`description`,`content`) VALUES (34,\'风控部-合同管理专员\',\'\',\'\',\'\',\'\',100,100,1420698337,\'风控部-合同管理专员\',\'风控部-合同管理专员\',\'<p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">所属部门：</span></strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">风控部&nbsp;<span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:Calibri\\\">&nbsp;</span><span style=\\\"font-family:宋体\\\">工作地点：</span></strong><span style=\\\"font-family:宋体\\\">长春&nbsp;</span><span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作性质：</span></strong><span style=\\\"font-family:宋体\\\">全职</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">岗位职责<span style=\\\"font-family:Calibri\\\">:</span></span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1<span style=\\\"font-family:宋体\\\">、进行跟进放款前条件落实审核，与合作机构进行沟通、联系，</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2<span style=\\\"font-family:宋体\\\">、对已签订合同进行审核登记、存档工作，确保其合同的合法、合规性；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3<span style=\\\"font-family:宋体\\\">、定期接收各城市门店上报的合同，进行整理、归档、登记，同时进行相应记录；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">4<span style=\\\"font-family:宋体\\\">、对合同状态、邮寄状态进行记录，及时掌握合同履行、变更、终止情况，并做好登记；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">5<span style=\\\"font-family:宋体\\\">、负责与业务人员沟通并及时处理因合同所发生的问题；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">6<span style=\\\"font-family:宋体\\\">、负责网站运营上线等基本操作；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">7<span style=\\\"font-family:宋体\\\">、负责领导安排的其他工作；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">任职资格：</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1<span style=\\\"font-family:宋体\\\">、大学本科以上学历，具有两年以上贷款行业相关工作经验；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2<span style=\\\"font-family:宋体\\\">、能够熟练使用办公软件，制作各种报表；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3<span style=\\\"font-family:宋体\\\">、工作态度端正，工作积极、主动，具有较强的责任心，具有团队合作精神和创新精神；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">4<span style=\\\"font-family:宋体\\\">、思维敏捷、思路清晰，具有良好的沟通能力；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">5<span style=\\\"font-family:宋体\\\">、法律及行政管理专业优先考虑。</span></span></p><p><br/></p>\')','0');
