INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1084','添加信息成功','【后台用户：admin】添加信息成功。','1','1420698565','2','2','INSERT INTO `tp_info` (`pid`,`title`,`img`,`imgs`,`url`,`tel`,`sort`,`click`,`time`,`keyword`,`description`,`content`) VALUES (34,\'研发部-运维工程师\',\'\',\'\',\'\',\'\',100,100,1420698532,\'研发部-运维工程师\',\'研发部-运维工程师\',\'<p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">所属部门：</span></strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">研发部&nbsp;<span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作地点：</span></strong><span style=\\\"font-family:宋体\\\">长春</span><span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作性质：</span></strong><span style=\\\"font-family:宋体\\\">全职</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">工作职责：<span style=\\\"font-family:Calibri\\\">&nbsp;</span></span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1.&nbsp;<span style=\\\"font-family:宋体\\\">负责公司网站线上服务的例行上线与日常维护工作；</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2.&nbsp;<span style=\\\"font-family:宋体\\\">负责</span><span style=\\\"font-family:Calibri\\\">IDC</span><span style=\\\"font-family:宋体\\\">机房</span><span style=\\\"font-family:Calibri\\\">Linux</span><span style=\\\"font-family:宋体\\\">业务服务器的配置，维护，监控，调优，故障排除等；</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3.&nbsp;<span style=\\\"font-family:宋体\\\">负责服务器和应用的规划、权限管理、运行监控、性能调优、容灾恢复等工作；</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">4.&nbsp;<span style=\\\"font-family:宋体\\\">公司系统安全及网络安全规划及部署；</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:Calibri\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">任职要求：</span></strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:Calibri\\\">&nbsp;</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1.&nbsp;<span style=\\\"font-family:宋体\\\">正规高校计算机或者相关专业</span><span style=\\\"font-family:Calibri\\\">,</span><span style=\\\"font-family:宋体\\\">本科及以上学历，</span><span style=\\\"font-family:Calibri\\\">2</span><span style=\\\"font-family:宋体\\\">年以上运维相关工作经验；</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2.&nbsp;<span style=\\\"font-family:宋体\\\">精通</span><span style=\\\"font-family:Calibri\\\">Linux</span><span style=\\\"font-family:宋体\\\">系统，熟悉</span><span style=\\\"font-family:Calibri\\\">Linux</span><span style=\\\"font-family:宋体\\\">下服务的安装及配置；如</span><span style=\\\"font-family:Calibri\\\">:&nbsp;Apache</span><span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">Nginx</span><span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">Mail</span><span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">Mysql</span><span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">Ftp</span><span style=\\\"font-family:宋体\\\">等常见服务的安装与配置；</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3.&nbsp;<span style=\\\"font-family:宋体\\\">精通</span><span style=\\\"font-family:Calibri\\\">1</span><span style=\\\"font-family:宋体\\\">种以上的脚本语言，包括但不限于</span><span style=\\\"font-family:Calibri\\\">Shell</span><span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">PERL</span><span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">PHP</span><span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">Python</span><span style=\\\"font-family:宋体\\\">等；</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">4.&nbsp;<span style=\\\"font-family:宋体\\\">能够熟练排查运维过程中出现的服务故障、系统故障、网络故障；</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">5.&nbsp;<span style=\\\"font-family:宋体\\\">高度的责任感，较强的故障分析及排除能力，善于在工作中学习，能够承受工作压力；</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">6.&nbsp;<span style=\\\"font-family:宋体\\\">优先考虑精通</span><span style=\\\"font-family:Calibri\\\">LAMP</span><span style=\\\"font-family:宋体\\\">架构，熟悉</span><span style=\\\"font-family:Calibri\\\">PHP</span><span style=\\\"font-family:宋体\\\">，有相关大中型在线系统开发及维护经验者；</span></span></p><p><br/></p>\')','0');
