INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1088','添加信息成功','【后台用户：admin】添加信息成功。','1','1420698728','2','2','INSERT INTO `tp_info` (`pid`,`title`,`img`,`imgs`,`url`,`tel`,`sort`,`click`,`time`,`keyword`,`description`,`content`) VALUES (34,\'品控部-项目专员\',\'\',\'\',\'\',\'\',100,100,1420698701,\'品控部-项目专员\',\'品控部-项目专员\',\'<p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">所属部门：</span></strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">品控部&nbsp;<span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作地点：</span></strong><span style=\\\"font-family:宋体\\\">长春&nbsp;</span><span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作性质：</span></strong><span style=\\\"font-family:宋体\\\">全职</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">岗位要求：</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1<span style=\\\"font-family:宋体\\\">、从事信贷业务满</span><span style=\\\"font-family:Calibri\\\">1</span><span style=\\\"font-family:宋体\\\">年以上，有信贷审批经验、金融项目管理经验者优先；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2<span style=\\\"font-family:宋体\\\">、本科学历及以上。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">工作内容：</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1<span style=\\\"font-family:宋体\\\">、与合作机构的日常外联沟通，包括产品内容确认、展现、调整，日常运营过程中的问题沟通、协商和解决等；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2<span style=\\\"font-family:宋体\\\">、与业务相关部门的内部沟通，包括日常业务操作沟通等；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3<span style=\\\"font-family:宋体\\\">、对门店发起的订单进行初步审核；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">4<span style=\\\"font-family:宋体\\\">、根据用户资质，提供相应的信贷服务；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">5<span style=\\\"font-family:宋体\\\">、完成领导安排的其他任务。</span></span></p><p><br/></p>\')','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1089','添加信息成功','【后台用户：admin】添加信息成功。','1','1420698761','2','2','INSERT INTO `tp_info` (`pid`,`title`,`img`,`imgs`,`url`,`tel`,`sort`,`click`,`time`,`keyword`,`description`,`content`) VALUES (34,\'数据部-数据仓库架构师\',\'\',\'\',\'\',\'\',100,100,1420698735,\'数据部-数据仓库架构师\',\'数据部-数据仓库架构师\',\'<p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">所属部门：</span></strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">数据部&nbsp;<span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:Calibri\\\">&nbsp;</span><span style=\\\"font-family:宋体\\\">工作地点：</span></strong><span style=\\\"font-family:宋体\\\">长春&nbsp;</span><span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作性质：</span></strong><span style=\\\"font-family:宋体\\\">全职</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">工作职责：</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1<span style=\\\"font-family:宋体\\\">、负责数据仓库设计架构、建模，提供相应解决方案。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2<span style=\\\"font-family:宋体\\\">、负责与业务部门沟通数据需求，并设计业务数据报表。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3<span style=\\\"font-family:宋体\\\">、上级交代的其他工作</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">职位要求：</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1.&nbsp;<span style=\\\"font-family:宋体\\\">计算机、数学等相关专业本科及以上学历；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2.&nbsp;&nbsp;2<span style=\\\"font-family:宋体\\\">年以上数据仓库领域工作经验，对数据仓库系统架构具有良好的认识，熟悉数据仓库相关技术，如</span><span style=\\\"font-family:Calibri\\\">&nbsp;ETL</span><span style=\\\"font-family:宋体\\\">、报表开发，具备数据分析技术并具有相关项目经验；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3.&nbsp;<span style=\\\"font-family:宋体\\\">熟悉数据库技术，理解数据库原理与数据建模理论，对性能调优有一定的经验心得；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">4.&nbsp;<span style=\\\"font-family:宋体\\\">具有</span><span style=\\\"font-family:Calibri\\\">MYSQL</span><span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">ORACLE/DB2/SYBASE</span><span style=\\\"font-family:宋体\\\">等至少一种大型数据库的应用开发经验，熟悉数据标准管理、元数据管理、数据质量管理；对整体数据有综合的把控能力。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">5.&nbsp;<span style=\\\"font-family:宋体\\\">熟练使用建模工具优先（如</span><span style=\\\"font-family:Calibri\\\">PD</span><span style=\\\"font-family:宋体\\\">）。</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">6.&nbsp;<span style=\\\"font-family:宋体\\\">负责或参与过大中型系统的数据模型和数据逻辑结构分析和设计，有大规模数据分析和模型设计方面的经验优先，有互联网、银行、电信数据中心应用项目经验优先考虑，；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">7.&nbsp;<span style=\\\"font-family:宋体\\\">具备良好的沟通能力和团队合作精神，能承担一定的工作压力。</span></span></p><p><br/></p>\')','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1090','添加信息成功','【后台用户：admin】添加信息成功。','1','1420698796','2','2','INSERT INTO `tp_info` (`pid`,`title`,`img`,`imgs`,`url`,`tel`,`sort`,`click`,`time`,`keyword`,`description`,`content`) VALUES (34,\'数据部-数据分析师\',\'\',\'\',\'\',\'\',100,100,1420698768,\'数据部-数据分析师\',\'数据部-数据分析师\',\'<p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">所属部门：</span></strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">数据部&nbsp;<span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作地点：</span></strong><span style=\\\"font-family:宋体\\\">长春</span><span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作性质：</span></strong><span style=\\\"font-family:宋体\\\">全职</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">工作内容：</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1.&nbsp;<span style=\\\"font-family:宋体\\\">负责项目的需求调研、数据分析、商业分析和数据挖掘模型等，通过对用户的行为进行分析了解用户的需求；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2.&nbsp;<span style=\\\"font-family:宋体\\\">根据各业务部门的实际业务要求完成较深入的专项数据分析及用户研究</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3.&nbsp;<span style=\\\"font-family:宋体\\\">定期梳理数据需求文档，从中抽象出可通用的数据模型，推动数据驱动业务决策的转化</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">4.&nbsp;<span style=\\\"font-family:宋体\\\">独立研究数据挖掘模型，参与数据挖掘模型的构建、维护、部署和评估，引入数据分析方法及模型到实际分析工作当中</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">5.&nbsp;<span style=\\\"font-family:宋体\\\">通过对公司运营数据研究，提出改善运营质量的方法和建议，搭建</span><span style=\\\"font-family:Calibri\\\">BI</span><span style=\\\"font-family:宋体\\\">数据分析体系，为公司决策提供支持</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">任职要求</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1<span style=\\\"font-family:宋体\\\">、统计学、数学或计算机、数理统计或数据挖掘专业方向相关专业大学本科以上学历；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2<span style=\\\"font-family:宋体\\\">、互联网</span><span style=\\\"font-family:Calibri\\\">2</span><span style=\\\"font-family:宋体\\\">年以上数据分析、数据挖掘经验，；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3<span style=\\\"font-family:宋体\\\">、熟练使用数理统计、数据分析、数据挖掘工具软件（如</span><span style=\\\"font-family:Calibri\\\">SPSS</span><span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">Clementine/SAS&nbsp;EM</span><span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">EXCEL</span><span style=\\\"font-family:宋体\\\">、</span><span style=\\\"font-family:Calibri\\\">EVIEW</span><span style=\\\"font-family:宋体\\\">等），熟练使用</span><span style=\\\"font-family:Calibri\\\">SQL</span><span style=\\\"font-family:宋体\\\">；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">4<span style=\\\"font-family:宋体\\\">、思维敏捷，具有发散性，能够举一反三，良好的逻辑分析能力及问题解决能力</span><span style=\\\"font-family:Calibri\\\">;</span><span style=\\\"font-family:宋体\\\">良好的跨部门沟通及组织能力，积极发扬团队合作精神；</span></span></p><p><br/></p>\')','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1091','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699048','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'数据部-数据分析师\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1420698768,`keyword`=\'数据部-数据分析师\',`description`=\'数据部-数据分析师\',`content`=\'<p><strong>所属部门：</strong>数据部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; <strong>工作性质：</strong>全职</p><p><br/></p><p><strong>工作内容：</strong></p><p>1. 负责项目的需求调研、数据分析、商业分析和数据挖掘模型等，通过对用户的行为进行分析了解用户的需求；</p><p>2. 根据各业务部门的实际业务要求完成较深入的专项数据分析及用户研究</p><p>3. 定期梳理数据需求文档，从中抽象出可通用的数据模型，推动数据驱动业务决策的转化</p><p>4. 独立研究数据挖掘模型，参与数据挖掘模型的构建、维护、部署和评估，引入数据分析方法及模型到实际分析工作当中</p><p>5. 通过对公司运营数据研究，提出改善运营质量的方法和建议，搭建BI数据分析体系，为公司决策提供支持</p><p><br/></p><p><strong>任职要求</strong></p><p>1、统计学、数学或计算机、数理统计或数据挖掘专业方向相关专业大学本科以上学历；</p><p>2、互联网2年以上数据分析、数据挖掘经验，；</p><p>3、熟练使用数理统计、数据分析、数据挖掘工具软件（如SPSS、Clementine/SAS EM、EXCEL、EVIEW等），熟练使用SQL；</p><p>4、思维敏捷，具有发散性，能够举一反三，良好的逻辑分析能力及问题解决能力;良好的跨部门沟通及组织能力，积极发扬团队合作精神；</p><p><br/></p>\' WHERE ( id = 1074 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1092','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699088','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'数据部-数据仓库架构师\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1420698735,`keyword`=\'数据部-数据仓库架构师\',`description`=\'数据部-数据仓库架构师\',`content`=\'<p><strong>所属部门：</strong>数据部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; &nbsp;<strong>工作性质：</strong>全职</p><p><br/></p><p><strong>工作职责：</strong></p><p>1、负责数据仓库设计架构、建模，提供相应解决方案。</p><p>2、负责与业务部门沟通数据需求，并设计业务数据报表。</p><p>3、上级交代的其他工作</p><p><br/></p><p><strong>职位要求：</strong></p><p>1. 计算机、数学等相关专业本科及以上学历；</p><p>2. &nbsp;2年以上数据仓库领域工作经验，对数据仓库系统架构具有良好的认识，熟悉数据仓库相关技术，如 ETL、报表开发，具备数据分析技术并具有相关项目经验；</p><p>3. 熟悉数据库技术，理解数据库原理与数据建模理论，对性能调优有一定的经验心得；</p><p>4. 具有MYSQL、ORACLE/DB2/SYBASE等至少一种大型数据库的应用开发经验，熟悉数据标准管理、元数据管理、数据质量管理；对整体数据有综合的把控能力。</p><p>5. 熟练使用建模工具优先（如PD）。</p><p>6. 负责或参与过大中型系统的数据模型和数据逻辑结构分析和设计，有大规模数据分析和模型设计方面的经验优先，有互联网、银行、电信数据中心应用项目经验优先考虑，；</p><p>7. 具备良好的沟通能力和团队合作精神，能承担一定的工作压力。</p><p><br/></p>\' WHERE ( id = 1073 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1093','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699124','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'品控部-项目专员\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1420698701,`keyword`=\'品控部-项目专员\',`description`=\'品控部-项目专员\',`content`=\'<p><strong>所属部门：</strong>品控部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; &nbsp;<strong>工作性质：</strong>全职</p><p><br/></p><p><strong>岗位要求：</strong></p><p>1、从事信贷业务满1年以上，有信贷审批经验、金融项目管理经验者优先；</p><p>2、本科学历及以上。</p><p><br/></p><p><strong>工作内容：</strong></p><p>1、与合作机构的日常外联沟通，包括产品内容确认、展现、调整，日常运营过程中的问题沟通、协商和解决等；</p><p>2、与业务相关部门的内部沟通，包括日常业务操作沟通等；</p><p>3、对门店发起的订单进行初步审核；</p><p>4、根据用户资质，提供相应的信贷服务；</p><p>5、完成领导安排的其他任务。</p><p><br/></p>\' WHERE ( id = 1072 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1094','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699147','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'市场部-广告设计\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1420698661,`keyword`=\'市场部-广告设计\',`description`=\'市场部-广告设计\',`content`=\'<p><strong>所属部门：</strong>市场部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; <strong>工作性质：</strong>全职</p><p><br/></p><p><strong>岗位职责：</strong></p><p>1、设计网站的风格、色彩、布局、图片和 Flash；熟悉网页内容结构规划、内容比重分配；</p><p>2、负责网页，图片，动画的制作工作；</p><p>3、为网站页面提供设计方案，提供完整的平面效果图、Flash效果、并制作网站页面。</p><p><br/></p><p><strong>任职资格：</strong></p><p>1.具备3年以上互联网广告创意、设计经验；</p><p>2.有独立完成创意设计类项目的经验，熟练运用各种不同类型的设计表现形式，准确传达信息；</p><p>3.熟悉landing page创意及广告素材设计、优化流程；</p><p>4.具备一定的广告文案组织、撰写能力；</p><p>5.可通过axure、visio、mockups等软件生成的网页原型了解设计需求。</p><p><br/></p>\' WHERE ( id = 1071 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1095','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699173','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'研发部-安卓开发工程师\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1420698619,`keyword`=\'研发部-安卓开发工程师\',`description`=\'研发部-安卓开发工程师\',`content`=\'<p><strong>所属部门：</strong>研发部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; &nbsp;<strong>工作性质：</strong>全职</p><p><br/></p><p><strong>工作职责：</strong></p><p>1.快速进行Android APP研发，包括金投资贷款APP，办卡APP等</p><p>2.能依据项目进度与需求，按时完成所需功能开发</p><p>3.团队合作，研发稳定，高性能的产品</p><p><br/></p><p><strong>任职要求：</strong></p><p>1.2年以上JAVA经验，1年以上Android开发经验</p><p>2.精通Android开发平台、框架原理及基本组件，能独立进行Android应用开发</p><p>3.熟悉常用设计模式，熟练使用Android各种布局与控件</p><p>4.对移动领域新技术有较强的学习能力、独立解决问题的能力</p><p><br/></p>\' WHERE ( id = 1070 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1096','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699209','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'研发部-IOS客户端开发工程师\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1420698572,`keyword`=\'研发部-IOS客户端开发工程师\',`description`=\'研发部-IOS客户端开发工程师\',`content`=\'<p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"></span></strong></p><p><strong>所属部门：</strong>研发部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; <strong>工作性质：</strong>全职</p><p><br/></p><p><strong>工作职责：</strong></p><p>1.快速进行IOS APP 研发，包括包括金投资贷款APP，办卡APP等</p><p>2.能依据项目进度与需求，按时完成所需功能开发</p><p>3.团队合作，研发稳定，高性能的产品</p><p><br/></p><p><strong>任职要求：</strong></p><p>1.有良好的C/C++语言基础，精通Objective-C语言</p><p>2.深入了解IOS系统，熟悉http协议</p><p>3.深刻理解oop，了解设计模式，有良好的编程风格和文档习惯</p><p>4.具有一年以上IOS开发经验</p><p>5.具有良好的表达和沟通能力，有较强的团队合作能力和责任心</p><p>6.较好的逻辑思维分析和表达及沟通能力，具有一定的自学能力，较强的团队协作意识</p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"></span></span><br/></p><p><br/></p>\' WHERE ( id = 1069 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1097','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699239','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'研发部-运维工程师\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1420698532,`keyword`=\'研发部-运维工程师\',`description`=\'研发部-运维工程师\',`content`=\'<p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"></span></strong></p><p><strong>所属部门：</strong>研发部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; <strong>工作性质：</strong>全职</p><p><br/></p><p><strong>工作职责：</strong>&nbsp;</p><p>1. 负责公司网站线上服务的例行上线与日常维护工作；&nbsp;</p><p>2. 负责IDC机房Linux业务服务器的配置，维护，监控，调优，故障排除等；&nbsp;</p><p>3. 负责服务器和应用的规划、权限管理、运行监控、性能调优、容灾恢复等工作；&nbsp;</p><p>4. 公司系统安全及网络安全规划及部署；&nbsp;</p><p><br/></p><p><strong>任职要求：</strong>&nbsp;</p><p>1. 正规高校计算机或者相关专业,本科及以上学历，2年以上运维相关工作经验；&nbsp;</p><p>2. 精通Linux系统，熟悉Linux下服务的安装及配置；如: Apache、Nginx、Mail、Mysql、Ftp等常见服务的安装与配置；&nbsp;</p><p>3. 精通1种以上的脚本语言，包括但不限于Shell、PERL、PHP、Python等；&nbsp;</p><p>4. 能够熟练排查运维过程中出现的服务故障、系统故障、网络故障；&nbsp;</p><p>5. 高度的责任感，较强的故障分析及排除能力，善于在工作中学习，能够承受工作压力；&nbsp;</p><p>6. 优先考虑精通LAMP架构，熟悉PHP，有相关大中型在线系统开发及维护经验者；</p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"></span></span><br/></p><p><br/></p>\' WHERE ( id = 1068 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1098','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699277','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'研发部-ERP系统工程师\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1420698490,`keyword`=\'研发部-ERP系统工程师\',`description`=\'研发部-ERP系统工程师\',`content`=\'<p><strong>所属部门：</strong>研发部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; <strong>工作性质：</strong>全职</p><p><br/></p><p><strong>岗位职责：</strong></p><p>负责融金投资EPR、内网等系统的建设、维护。</p><p><br/></p><p><strong>任职要求 ：</strong> &nbsp; &nbsp;</p><p>1. 计算机相关专业，熟练掌握java、PHP中的至少一门语言；</p><p>2. 有过ERP（或OA）系统的开发、二次开发或实施的经验；</p><p>3. 较好的沟通能力、团队合作精神，自信、有激情（加分项）；</p><p>4. 熟悉DISCUZ系统；</p><p>5. 熟悉MySQL/ORACLE/POSTGRESQL任意一种；</p><p>6. 有Linux下开发经验，熟悉Linux常用操作。</p><p><br/></p>\' WHERE ( id = 1067 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1099','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699309','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'研发部-安卓研发工程师\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1420698439,`keyword`=\'研发部-安卓研发工程师\',`description`=\'研发部-安卓研发工程师\',`content`=\'<p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"></span></strong></p><p><strong>所属部门：</strong>研发部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; &nbsp;<strong>工作性质：</strong>全职</p><p><br/></p><p><strong>岗位要求：</strong></p><p>承担金投资安卓app的研发开发工作</p><p><br/></p><p><strong>要求：</strong></p><p>1、2年以上java相关经验；1年以上Android开发经验</p><p>2、精通Android开发平台及框架原理，能独立进行Android应用程序开发，精通Android的基本组件使用</p><p>3、熟悉java，熟悉常用设计模式，有个人独立开发完整产品经验者优先</p><p>4、熟练使用Android各种布局与控件，熟悉各种动画场景的运用</p><p>5、对移动领域新技术有较强的学习能力、独立解决问题的能力</p><p>6、热爱互联网金融优先</p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"></span></span><br/></p><p><br/></p>\' WHERE ( id = 1066 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1100','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699335','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'风控部-房款管理专员\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1420698404,`keyword`=\'风控部-房款管理专员\',`description`=\'风控部-房款管理专员\',`content`=\'<p><strong>所属部门：</strong>风控部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; <strong>工作性质：</strong>全职</p><p><br/></p><p><strong>岗位职责：</strong></p><p>1．根据公司要求，对贷款放款前、放款后条件的落实进行审核，与合作机构进行沟通、联系，</p><p>2．定期接收各城市门店上报的合同，进行整理、归档、登记，同时进行相应记录；</p><p>3．对合同状态、邮寄状态进行记录，及时掌握合同履行、变更、终止情况，并做好登记；、</p><p>4．对已签订的合同进行登记、存档，确保合同签订完整性、规范性</p><p>5．负责与业务人员沟通并及时处理因合同所发生的各项问题；</p><p>6．负责领导安排的其他工作；</p><p><br/></p><p><strong>任职要求：</strong></p><p>1. 大学本科及以上学历,金融、法律、财会类或经济类等相关专业毕业优先；</p><p>2. 具备1-2年贷款管理经验；</p><p>3. 工作主动负责，认真仔细，勤勉敬业，善于沟通，有较强的客户服务意识； 具有良好的客户沟通、人际交往及维系客户关系的能力。</p><p><br/></p>\' WHERE ( id = 1065 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1101','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699363','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'风控部-合同管理专员\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1420698337,`keyword`=\'风控部-合同管理专员\',`description`=\'风控部-合同管理专员\',`content`=\'<p><strong>所属部门：</strong>风控部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; &nbsp;<strong>工作性质：</strong>全职</p><p><br/></p><p><strong>岗位职责:</strong></p><p>1、进行跟进放款前条件落实审核，与合作机构进行沟通、联系，</p><p>2、对已签订合同进行审核登记、存档工作，确保其合同的合法、合规性；</p><p>3、定期接收各城市门店上报的合同，进行整理、归档、登记，同时进行相应记录；</p><p>4、对合同状态、邮寄状态进行记录，及时掌握合同履行、变更、终止情况，并做好登记；</p><p>5、负责与业务人员沟通并及时处理因合同所发生的问题；</p><p>6、负责网站运营上线等基本操作；</p><p>7、负责领导安排的其他工作；</p><p><br/></p><p><strong>任职资格：</strong></p><p>1、大学本科以上学历，具有两年以上贷款行业相关工作经验；</p><p>2、能够熟练使用办公软件，制作各种报表；</p><p>3、工作态度端正，工作积极、主动，具有较强的责任心，具有团队合作精神和创新精神；</p><p>4、思维敏捷、思路清晰，具有良好的沟通能力；</p><p>5、法律及行政管理专业优先考虑。</p><p><br/></p>\' WHERE ( id = 1064 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1102','新用户注册','【前台会员：WANGYU】注册成功','179','1420699373','1','1','INSERT INTO `tp_web_user` (`pid`,`user_name`,`phone`,`user_pwd`,`email`,`referer`,`code_number`,`is_effect`,`create_time`,`bonus_money`,`is_phone`,`score`) VALUES (0,\'WANGYU\',\'13604330743\',\'9db45885f671c8c74b5b0d7db4eb04f1\',\'398067630@qq.com\',null,3338,1,1420699373,50,1,20)','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1103','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699386','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'风控部-审核专员\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1420698299,`keyword`=\'风控部-审核专员\',`description`=\'风控部-审核专员\',`content`=\'<p><strong>所属部门：</strong>风控部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; &nbsp;<strong>工作性质：</strong>全职</p><p><br/></p><p><strong>岗位职责：</strong></p><p>1、审核借款申请人资料的真实性、完整性；</p><p>2、按要求对借款申请人进行调查，审核借款人的资信条件，形成初步审核意见；</p><p>3、与其他部门同事合作，完善审核流程，加强风险管理，降低风险及信贷损失。</p><p>4、在工作中积极提出自己的意见和建议，优化工作流程。</p><p>5、领导交予的其他工作</p><p><br/></p><p><strong>任职要求：</strong></p><p>1. 大学本科及以上学历,金融、法律、财会类或经济类等相关专业毕业优先；</p><p>2. 具备2年以上同行业信贷审查相关知识技能、授信审查审批、风险管理等工作经验者优先；</p><p>3. 熟悉信贷业务及相关法规、制度，具有极强的分析能力及风险防范能力，有良好的职业操守，无不良工作记录；</p><p>4、工作主动负责，认真仔细，勤勉敬业，善于沟通，有较强的客户服务意识； 具有良好的客户沟通、人际交往及维系客户关系的能力。</p><p><br/></p>\' WHERE ( id = 1063 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1104','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699410','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'风控部-复审\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1420698268,`keyword`=\'风控部-复审\',`description`=\'风控部-复审\',`content`=\'<p><strong>所属部门：</strong>风控部 &nbsp; &nbsp; <strong>&nbsp;工作地点：</strong>长春 &nbsp; &nbsp; &nbsp;<strong>工作性质：</strong>全职</p><p><br/></p><p><strong>岗位职责：</strong></p><p>1、审核借款申请人资料的真实性、完整性；</p><p>2、对借款申请人各项资料进行复合，形成终审意见；</p><p>3、对初审给予指导意见；</p><p>4、与其他部门同事合作，完善审核流程，加强风险管理，降低风险及信贷损失。</p><p>5、在工作中积极提出自己的意见和建议，优化工作流程。</p><p>6、领导交予的其他工作</p><p><br/></p><p><strong>任职要求：</strong></p><p>1. 大学本科及以上学历,金融、法律、财会类或经济类等相关专业毕业优先；</p><p>2. 具备3-5年以上同行业信贷审查相关知识技能、授信审查审批、风险管理等工作经验者优先；</p><p>3. 熟悉信贷业务及相关法规、制度，具有极强的分析能力及风险防范能力，有良好的职业操守，无不良工作记录；</p><p>4、工作主动负责，认真仔细，勤勉敬业，善于沟通，有较强的客户服务意识； 具有良好的客户沟通、人际交往及维系客户关系的能力。</p><p><br/></p>\' WHERE ( id = 1062 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1105','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699444','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'风控部-算法和机器学习架构师\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1420698224,`keyword`=\'风控部-算法和机器学习架构师\',`description`=\'风控部-算法和机器学习架构师\',`content`=\'<p><strong>所属部门：</strong>风控部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; &nbsp;<strong>工作性质：</strong>全职</p><p><br/></p><p><strong>岗位职责：</strong></p><p>1.从个人征信数据、行为数据、互联网数据等出发，构建大数据金融模型用于贷款自动化授信，以及相关数据和模型建设</p><p>2.带领完成从模型构建到工程实现的整体设计、实现工作</p><p>3.协助规划构建相关技术线团队</p><p><br/></p><p><strong>任职要求</strong></p><p>1.很熟练的掌握统计机器学习方向技能（如：决策树、逻辑回归、神经网络、图模型，概率统计，最优化方法等），有良好的建模能力。</p><p>2.熟悉互联网数据挖掘经验者优先（如数据抓取、文本挖掘、分类、NLP方向）</p><p>3.统计、数学、运筹学或计算机数据挖掘/机器学习方向 硕士毕业</p><p>4.有良好的工程实现能力，有2年以上代码工程实践经验，有多个产品在互联网平台上线经验</p><p>5.熟悉一门或几门以下技术:C/C++,Linux/Unix Shell,Python,PHP编程。 会用SAS或SPSS、R语言。</p><p>6.互联网公司工作经验不低于3年</p><p><br/></p>\' WHERE ( id = 1061 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1106','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699469','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'风控部-贷款专员\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1420698186,`keyword`=\'风控部-贷款专员\',`description`=\'风控部-贷款专员\',`content`=\'<p><strong>所属部门：</strong>风控部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; &nbsp;<strong>工作性质：</strong>全职</p><p><br/></p><p><strong>岗位职责</strong></p><p>&nbsp;1、支持融360的风控业务；在网点与贷款客户沟通并访谈；检查与核算客户资料，并进行资料录入；</p><p>2、 偶尔到客户地点进行调研；无销售业绩指标。</p><p><br/></p><p><strong>任职要求</strong></p><p>1、 大专以上学历；良好的沟通能力和人际关系能力；</p><p>2、 基本电脑知识（含office）；有客户服务经验（零售、中介等）更佳；</p><p>3、 良好的学习能力，团队意识强。</p><p><br/></p>\' WHERE ( id = 1060 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1107','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699493','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'编辑部-金融产品分析师\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1419474731,`keyword`=\'编辑部-金融产品分析师\',`description`=\'编辑部-金融产品分析师\',`content`=\'<p><strong>所属部门：</strong>编辑部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; <strong>工作性质：</strong>全职</p><p><br/></p><p><strong>岗位职责：</strong></p><p>1、 负责内容运营部专业报告的框架搭建及报告撰写工作；</p><p>2、 依据报告要求完成数据采集的数据要求及规范工作，并负责采集到数据的价值挖掘工作。</p><p><br/></p><p><strong>任职要求：</strong></p><p>1、 金融或统计相关专业本科以上学历；</p><p>2、 有至少五年以上的专业报告撰写经验；</p><p>3、 有至少五年以上的金融产品数据分析经验；</p><p>4、 有严谨的工作态度，对数据具有高度敏感性。</p><p><br/></p>\' WHERE ( id = 964 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1108','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699515','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'编辑部-理财数据运营专员\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1414992929,`keyword`=\'编辑部-理财数据运营专员\',`description`=\'编辑部-理财数据运营专员\',`content`=\'<p><strong>所属部门：</strong>编辑部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; &nbsp;<strong>工作性质：</strong>全职</p><p><br/></p><p><strong>岗位职责：</strong></p><p>1、负责融360理财频道网贷投资、互联网理财、银行理财产品的数据搜集、整理；</p><p>2、对融360理财平台数据库进行分析，提炼出对用户、机构有价值的数据信息。</p><p><br/></p><p><strong>任职要求：</strong></p><p>1、1年以上理财相关行业工作经历（第三方理财、理财编辑等）；</p><p>2、了解网贷投资、互联网理财、银行理财；</p><p>3、有较好的沟通能力，有过数据整理和分析经验；</p><p>4、本科以上学历。</p><p><br/></p>\' WHERE ( id = 80 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1109','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699543','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'BC部-下贷款推广\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1414992896,`keyword`=\'BC部-下贷款推广\',`description`=\'BC部-下贷款推广\',`content`=\'<p><strong>所属部门：</strong>BC部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp;<strong>工作性质：</strong>全职</p><p><br/></p><p><strong>岗位职责：</strong></p><p>1、负责开拓贷款客户渠道，寻找目标客户群；</p><p>2、根据公司要求完成销售业绩。</p><p>3、为客户提供贷款的种类、期限、利率、条件和所需的材料咨询服务。</p><p>4、指导客户填写贷款申请、各种表格等资料，并负责受理借款申请和审核所需的材料；严格审核借款人的真实性、手续的规范性、贷款使用的准确性。</p><p>5、到户到企业调查，主要对借款人的资信等级以及借款的合法性、真实性、营利性等情况进行调查，核实抵押物、质押物、保证人情况。</p><p>6、努力完成本职工作，按照规范化、制度化、程序化的要求，做好贷款客户资料的整理、立卷归档工作。</p><p>7、遵循贷款市场要求和营销规则，提高经营效益。</p><p>8、完成领导交办的其他事项。</p><p>9、按照公司制度明确落实完成工作。</p><p><br/></p><p><strong>任职要求：</strong></p><p>1、1-2年以上销售行业工作经验，业绩突出者优先；</p><p>2、反应敏捷、表达能力强，具有较强的沟通能力及交际技巧，具有亲和力；</p><p>3、.具备一定的市场分析及判断能力，良好的客户服务意识；</p><p>4、有责任心，能承受较大的工作压力；</p><p>5、有团队协作精神，善于挑战。</p><p>6、大专以上学历，有经验者优先</p><p><br/></p>\' WHERE ( id = 79 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1110','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699575','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'BC部-后端运营\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=101,`time`=1411020396,`keyword`=\'BC部-后端运营\',`description`=\'BC部-后端运营\',`content`=\'<p><strong>所属部门：</strong>BC部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; &nbsp;<strong>工作性质：</strong>全职</p><p><br/></p><p><strong>岗位职责：</strong></p><p>1、负责对已有的客户资源进行专业的管理和维护，与客户建立良好的关系。 &nbsp; &nbsp;</p><p>2、重点客户拓展再营销 &nbsp; &nbsp;</p><p>3、搜集市场信息 &nbsp; &nbsp;</p><p>4、 具备对各类客户采用不策略和工具的判断能力，确保充值金额和反馈率的最大化</p><p><br/></p><p><strong>任职要求：</strong></p><p>1、大专及以上学历，营销、管理、金融等相关专业优先；</p><p>2、形象良好，具有良好的客户沟通、人际交往及维系客户关系的能力；</p><p>3、诚实守信，品行良好；能够有效开发客户资源；</p><p>4、勤奋努力，具有吃苦耐劳以达成工作目标的毅力和决心。</p><p><br/></p>\' WHERE ( id = 71 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1111','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699659','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'编辑部-理财数据运营专员\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1414992929,`keyword`=\'编辑部-理财数据运营专员\',`description`=\'编辑部-理财数据运营专员\',`content`=\'<p><strong>所属部门：</strong>编辑部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; &nbsp;<strong>工作性质：</strong>全职</p><p><br/></p><p><strong>岗位职责：</strong></p><p>1、负责金投资理财频道网贷投资、互联网理财、银行理财产品的数据搜集、整理；</p><p>2、对金投资理财平台数据库进行分析，提炼出对用户、机构有价值的数据信息。</p><p><br/></p><p><strong>任职要求：</strong></p><p>1、1年以上理财相关行业工作经历（第三方理财、理财编辑等）；</p><p>2、了解网贷投资、互联网理财、银行理财；</p><p>3、有较好的沟通能力，有过数据整理和分析经验；</p><p>4、本科以上学历。</p><p><br/></p>\' WHERE ( id = 80 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1112','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699677','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'风控部-贷款专员\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1420698186,`keyword`=\'风控部-贷款专员\',`description`=\'风控部-贷款专员\',`content`=\'<p><strong>所属部门：</strong>风控部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; &nbsp;<strong>工作性质：</strong>全职</p><p><br/></p><p><strong>岗位职责</strong></p><p>&nbsp;1、支持金投资的风控业务；在网点与贷款客户沟通并访谈；检查与核算客户资料，并进行资料录入；</p><p>2、 偶尔到客户地点进行调研；无销售业绩指标。</p><p><br/></p><p><strong>任职要求</strong></p><p>1、 大专以上学历；良好的沟通能力和人际关系能力；</p><p>2、 基本电脑知识（含office）；有客户服务经验（零售、中介等）更佳；</p><p>3、 良好的学习能力，团队意识强。</p><p><br/></p>\' WHERE ( id = 1060 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1113','修改信息成功','【后台用户：admin】修改信息成功。','1','1420699862','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'研发部-运维工程师\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1420698532,`keyword`=\'研发部-运维工程师\',`description`=\'研发部-运维工程师\',`content`=\'<p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"></span></strong></p><p><strong>所属部门：</strong>研发部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; <strong>工作性质：</strong>全职</p><p><br/></p><p><strong>工作职责：</strong>&nbsp;</p><p>1. 负责公司网站线上服务的例行上线与日常维护工作；&nbsp;</p><p>2. 负责IDC机房Linux业务服务器的配置，维护，监控，调优，故障排除等；&nbsp;</p><p>3. 负责服务器和应用的规划、权限管理、运行监控、性能调优、容灾恢复等工作；&nbsp;</p><p>4. 公司系统安全及网络安全规划及部署；&nbsp;</p><p><br/></p><p><strong>任职要求：</strong>&nbsp;</p><p>1. 正规高校计算机或者相关专业,本科及以上学历，2年以上运维相关工作经验；&nbsp;</p><p>2. 精通Linux系统，熟悉Linux下服务的安装及配置；如: Apache、Nginx、Mail、Mysql、Ftp等常见服务的安装与配置；&nbsp;</p><p>3. 精通1种以上的脚本语言，包括但不限于Shell、PERL、PHP、Python等；&nbsp;</p><p>4. 能够熟练排查运维过程中出现的服务故障、系统故障、网络故障；&nbsp;</p><p>5. 高度的责任感，较强的故障分析及排除能力，善于在工作中学习，能够承受工作压力；&nbsp;</p><p>6. 优先考虑精通LAMP架构，熟悉PHP，有相关大中型在线系统开发及维护经验者；</p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"></span></span><br/></p><p><br/></p>\' WHERE ( id = 1068 )','0');
