INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1116','添加信息成功','【后台用户：admin】添加信息成功。','1','1420700043','2','2','INSERT INTO `tp_info` (`pid`,`title`,`img`,`imgs`,`url`,`tel`,`sort`,`click`,`time`,`keyword`,`description`,`content`) VALUES (34,\'研发部-IOS客户端开发工程师\',\'\',\'\',\'\',\'\',100,100,1420699985,\'研发部-IOS客户端开发工程师\',\'研发部-IOS客户端开发工程师\',\'<p><strong>所属部门：</strong>研发部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; <strong>工作性质：</strong>全职</p><p><br/></p><p><strong>工作职责：</strong></p><p>1.快速进行IOS APP 研发，包括包括融360贷款APP，办卡APP等</p><p>2.能依据项目进度与需求，按时完成所需功能开发</p><p>3.团队合作，研发稳定，高性能的产品</p><p><br/></p><p><strong>任职要求：</strong></p><p>1.有良好的C/C++语言基础，精通Objective-C语言</p><p>2.深入了解IOS系统，熟悉http协议</p><p>3.深刻理解oop，了解设计模式，有良好的编程风格和文档习惯</p><p>4.具有一年以上IOS开发经验</p><p>5.具有良好的表达和沟通能力，有较强的团队合作能力和责任心</p><p>6.较好的逻辑思维分析和表达及沟通能力，具有一定的自学能力，较强的团队协作意识</p><p><br/></p>\')','0');
