INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1119','添加信息成功','【后台用户：admin】添加信息成功。','1','1420700238','2','2','INSERT INTO `tp_info` (`pid`,`title`,`img`,`imgs`,`url`,`tel`,`sort`,`click`,`time`,`keyword`,`description`,`content`) VALUES (34,\'风控部-房款管理专员\',\'\',\'\',\'\',\'\',100,100,1420700215,\'风控部-房款管理专员\',\'风控部-房款管理专员\',\'<p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">所属部门：</span></strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">风控部&nbsp;<span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作地点：</span></strong><span style=\\\"font-family:宋体\\\">长春</span><span style=\\\"font-family:Calibri\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\\\"font-family:宋体\\\">工作性质：</span></strong><span style=\\\"font-family:宋体\\\">全职</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">岗位职责：</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1<span style=\\\"font-family:宋体\\\">．根据公司要求，对贷款放款前、放款后条件的落实进行审核，与合作机构进行沟通、联系，</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2<span style=\\\"font-family:宋体\\\">．定期接收各城市门店上报的合同，进行整理、归档、登记，同时进行相应记录；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3<span style=\\\"font-family:宋体\\\">．对合同状态、邮寄状态进行记录，及时掌握合同履行、变更、终止情况，并做好登记；、</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">4<span style=\\\"font-family:宋体\\\">．对已签订的合同进行登记、存档，确保合同签订完整性、规范性</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">5<span style=\\\"font-family:宋体\\\">．负责与业务人员沟通并及时处理因合同所发生的各项问题；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">6<span style=\\\"font-family:宋体\\\">．负责领导安排的其他工作；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\"><span style=\\\"font-family:宋体\\\"><br/></span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><strong><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">任职要求：</span></strong></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">1.&nbsp;<span style=\\\"font-family:宋体\\\">大学本科及以上学历</span><span style=\\\"font-family:Calibri\\\">,</span><span style=\\\"font-family:宋体\\\">金融、法律、财会类或经济类等相关专业毕业优先；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">2.&nbsp;<span style=\\\"font-family:宋体\\\">具备</span><span style=\\\"font-family:Calibri\\\">1-2</span><span style=\\\"font-family:宋体\\\">年贷款管理经验；</span></span></p><p style=\\\"margin-bottom:0;margin-top:0\\\"><span style=\\\";font-size:14px;font-family:&#39;宋体&#39;\\\">3.&nbsp;<span style=\\\"font-family:宋体\\\">工作主动负责，认真仔细，勤勉敬业，善于沟通，有较强的客户服务意识；</span><span style=\\\"font-family:Calibri\\\">&nbsp;</span><span style=\\\"font-family:宋体\\\">具有良好的客户沟通、人际交往及维系客户关系的能力。</span></span></p><p><br/></p>\')','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1120','添加信息成功','【后台用户：admin】添加信息成功。','1','1420700287','2','2','INSERT INTO `tp_info` (`pid`,`title`,`img`,`imgs`,`url`,`tel`,`sort`,`click`,`time`,`keyword`,`description`,`content`) VALUES (34,\'研发部-安卓研发工程师\',\'\',\'\',\'\',\'\',100,100,1420700252,\'研发部-安卓研发工程师\',\'研发部-安卓研发工程师\',\'<p><strong>所属部门：</strong>研发部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; &nbsp;<strong>工作性质：</strong>全职</p><p><br/></p><p><strong>岗位要求：</strong></p><p>承担融360安卓app的研发开发工作</p><p><br/></p><p><strong>要求：</strong></p><p>1、2年以上java相关经验；1年以上Android开发经验</p><p>2、精通Android开发平台及框架原理，能独立进行Android应用程序开发，精通Android的基本组件使用</p><p>3、熟悉java，熟悉常用设计模式，有个人独立开发完整产品经验者优先</p><p>4、熟练使用Android各种布局与控件，熟悉各种动画场景的运用</p><p>5、对移动领域新技术有较强的学习能力、独立解决问题的能力</p><p>6、热爱互联网金融优先</p><p><br/></p>\')','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1121','修改信息成功','【后台用户：admin】修改信息成功。','1','1420700311','2','2','UPDATE `tp_info` SET `pid`=34,`title`=\'研发部-安卓研发工程师\',`img`=\'\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1420700252,`keyword`=\'研发部-安卓研发工程师\',`description`=\'研发部-安卓研发工程师\',`content`=\'<p><strong>所属部门：</strong>研发部 &nbsp; &nbsp; &nbsp;<strong>工作地点：</strong>长春 &nbsp; &nbsp; &nbsp;<strong>工作性质：</strong>全职</p><p><br/></p><p><strong>岗位要求：</strong></p><p>承担金投资安卓app的研发开发工作</p><p><br/></p><p><strong>要求：</strong></p><p>1、2年以上java相关经验；1年以上Android开发经验</p><p>2、精通Android开发平台及框架原理，能独立进行Android应用程序开发，精通Android的基本组件使用</p><p>3、熟悉java，熟悉常用设计模式，有个人独立开发完整产品经验者优先</p><p>4、熟练使用Android各种布局与控件，熟悉各种动画场景的运用</p><p>5、对移动领域新技术有较强的学习能力、独立解决问题的能力</p><p>6、热爱互联网金融优先</p><p><br/></p>\' WHERE ( id = 1077 )','0');
