INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1754','添加信息成功','【后台用户：admin】添加信息成功。','1','1422864008','2','2','INSERT INTO `tp_info` (`pid`,`title`,`img`,`imgs`,`url`,`tel`,`sort`,`click`,`time`,`keyword`,`description`,`reg_fund`,`company_create_time`,`join_time`,`company_url`,`company_tel`,`company_address`,`content`) VALUES (47,\'2014年10月，金湧博发荣获“吉林省AAA级诚信单位”\',\'\',\'\',\'\',\'\',100,100,1422863970,\'\',\'2014年10月，吉林省金湧博发投资管理有限公司荣获由《吉林日报》及吉林省百姓信誉评价组委会评选的“吉林省AAA级诚信单位”。《吉林日报》作为吉林省委机关报，是贴近基层、贴近群众、贴近生活的媒体。《吉林日报》“诚信单位”评选经企业自愿申报、组织推荐，并经吉林省百姓信誉评价组委会等相关部门共同审核和评选，予以殊荣。此次荣誉称号的获得，既是社会对金湧博发的认可，也体现了金湧博发对自身的严格要求，金湧博发将为了更好明天不断奋斗。\',\'\',\'\',\'\',\'\',\'\',\'\',\'<p>2014年10月，吉林省金湧博发投资管理有限公司荣获由《吉林日报》及吉林省百姓信誉评价组委会评选的“吉林省AAA级诚信单位”。《吉林日报》作为吉林省委机关报，是贴近基层、贴近群众、贴近生活的媒体。《吉林日报》“诚信单位”评选经企业自愿申报、组织推荐，并经吉林省百姓信誉评价组委会等相关部门共同审核和评选，予以殊荣。此次荣誉称号的获得，既是社会对金湧博发的认可，也体现了金湧博发对自身的严格要求，金湧博发将为了更好明天不断奋斗。</p><p><img src=\\\"/Uploads/201502/54cf2e85a7b98.jpg\\\" title=\\\"\\\" alt=\\\"荣誉A.jpg\\\"/></p>\')','0');
