INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1756','添加信息成功','【后台用户：admin】添加信息成功。','1','1422864166','2','2','INSERT INTO `tp_info` (`pid`,`title`,`img`,`imgs`,`url`,`tel`,`sort`,`click`,`time`,`keyword`,`description`,`reg_fund`,`company_create_time`,`join_time`,`company_url`,`company_tel`,`company_address`,`content`) VALUES (47,\'2014年8月，金湧博发——可信网站验证单位\',\'\',\'\',\'\',\'\',100,100,1422864119,\'\',\'“可信网站”验证是金湧博发取信于民的身份证。“可信网站”验证，是由中网推出的第三方网站真实身份验证服务，它通过对域名、网站、工商登记或组织机构信息进行严格交互审核来验证网站真实身份。金湧博发作为可信网站验证单位，不仅能帮助网站健全网络钓鱼防范措施，拓宽网站可信品牌的展示和推广力度；还能有效提高亿万网民辨别网站真伪和识破网络诈骗的能力。\',\'\',\'\',\'\',\'\',\'\',\'\',\'<p>“可信网站”验证是金湧博发取信于民的身份证。“可信网站”验证，是由中网推出的第三方网站真实身份验证服务，它通过对域名、网站、工商登记或组织机构信息进行严格交互审核来验证网站真实身份。金湧博发作为可信网站验证单位，不仅能帮助网站健全网络钓鱼防范措施，拓宽网站可信品牌的展示和推广力度；还能有效提高亿万网民辨别网站真伪和识破网络诈骗的能力。</p><p><br/></p><p><img src=\\\"/Uploads/201502/54cf2f219c47d.jpg\\\" title=\\\"\\\" alt=\\\"可信.jpg\\\"/></p>\')','0');
