INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1870','修改项目成功','【后台用户：cuikai】修改【项目信息：商贸公司资金周转】成功。','11','1423107234','2','2','UPDATE `tp_loan` SET `class_id`=0,`loan_title`=\'商贸公司资金周转\',`recommend`=1,`img`=\'/Uploads/201502/54d076c831781.jpg\',`loan_money`=500000,`min_loan_money`=0,`max_loan_money`=0,`loan_rate`=8,`loan_add_rate`=8,`loan_deadline`=1,`loan_deadline_type`=0,`end_time`=3,`repayment_mode`=\'经营收入\',`guarantee_mode`=\'担保公司\',`guarantee_purview`=\'全额担保\',`loan_purpose`=\'工程投入\',`pawn`=\'房产\',`production_value`=\'10000万\',`payment_source`=\'经营收入\',`repayment_ability`=\'优\',`loan_description`=\'<p>借款企业为长春本地家居商场，展厅面积9000+平米，由于预定五月一日开业，目前急需装修款进行内部装修打造体验店，同时由于具备双证可在银行申请直贷，但由于银行审批流程及抵押手续等原因在春节之前不能放款，为应对短期资金真空并赶工期装修故申请借款。</p>\',`company_name`=\'\',`company_info`=\'<p>借款企业为长春本地家居商场，展厅面积9000+平米，法人名下其他关联企业为：两座具有20余年装饰材料生产制造经验的大型工厂，其名下制造厂年产值一亿元左右，本项目位于长春最繁华商圈之一，日均客流10万人，目前土地证房产证俱全，是继长春太X城、红XX凯龙、欧X卖场之后又一专业销售家居中高端品牌商场，由于多年生产加工装饰材料，在全球拥有极其广泛的优质货源渠道，本企业采用O2O体验店结合方式注重成套家居体验，采用目前东北独家的线上销售线下交货模式经营，业务范围未来可迅速扩展整个吉林省。</p>\',`business_scope`=\'装饰材料生产制造，家居产品销售\',`business_state`=\'未来本企业除整体O2O销售模式外，在线下实体店采用自身经营+销售展位经营权形式，由其运用自身优质资源为购买展位的商户提供货源，目前每米暂定销售价格为2万/平米/15年，可回租可在期满溢价赎回。如在银行贷款失败不能短期履行还款合约的情况下，本企业的展位销售可作为强有力的还款来源。同时为提高违约成本和增加制约手段，由其名下其他关联企业（包括工厂、房地产开发公司）签署无限连带责任保证，做到万无一失。\',`guarantee_institution`=\'吉林省华兴泰丰信用担保有限公司\',`guarantee_content`=\'吉林省华兴泰丰信用担保有限公司是由中电华兴能源投资控股集团全资控股子公司，注册资金2亿元人民币\',`pawn_info`=\'抵押物为企业名下部分房产，以成本价抵押，总价约为100万左右\',`risk_control`=\'实物资产抵押\',`guarantee_idea`=\'\',`company_material`=\'\',`pawn_action`=\'/Uploads/201502/54d076c80f230.jpg\\r\\n/Uploads/201502/54d076c866dff.jpg\\r\\n/Uploads/201502/54d03a4c29d6f.jpg\',`contract_agreement`=\'\',`field_visit`=\'\',`guarantee_title`=\'\',`guarantee_measure`=\'\',`loan_order`=10,`deal_status`=1,`start_time`=1423533600,`bad_time`=0,`bad_msg`=\'\',`success_time`=0,`repay_confirm_time`=0,`is_effect`=1,`update_time`=1423107234,`update_by`=\'cuikai\' WHERE ( id=17 )','0');
