INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1925','修改信息成功','【后台用户：admin】修改信息成功。','1','1423121983','2','2','UPDATE `tp_info` SET `pid`=42,`title`=\'国付宝\',`img`=\'/Uploads/201502/54d31e38485b7.png\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1423118613,`keyword`=\'\',`description`=\'\',`reg_fund`=\'14285.72万元\',`company_create_time`=\'2011年1月25日\',`join_time`=\'2014年8月25日\',`company_url`=\'https://www.gopay.com.cn/\',`company_tel`=\'010-84681600\',`company_address`=\'北京市朝阳区霞光里9号中电发展大厦12层\',`content`=\'<p>国付宝信息科技有限公司成立于2011年1月25日，公司注册资本14285.72万元。由商务部中国国际电子商务中心与海航商业控股有限公司合作成立，主要经营第三方支付业务，互联网支付及移动电话支付（全国）。是针对政府及企业的需求和电子商务的发展，精心打造的国有背景的，引入社会诚信体系的独立第三方电子支付平台。采用国际上最先进的应用服务器和数据库系统，是非常权威、稳定、安全的第三方国家级电子商务全程服务平台。</p>\' WHERE ( id = 1272 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1926','修改信息成功','【后台用户：xiaojie】修改信息成功。','7','1423122053','2','2','UPDATE `tp_info` SET `pid`=42,`title`=\'吉林中城银信资产管理有限公司\',`img`=\'/Uploads/201502/54d30d0885647.png\',`imgs`=\'\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=100,`time`=1423117554,`keyword`=\'\',`description`=\'\',`reg_fund`=\'10000万元\',`company_create_time`=\'2013年\',`join_time`=\'2014年12月24日\',`company_url`=\'\',`company_tel`=\'\',`company_address`=\'长春市宽城区新发路1169号松苑宾馆B座\',`content`=\'<p>吉林中城银信资产管理有限公司成立于2013年，是隶属于中国城市建设控股集团的专业从事资产管理的大型非银行金融企业。主营大、中型企业提供资产管理、投资管理、股权投资、企业的重组并购、财务顾问、投资顾问等等业务。董事长是有27年金融从业经验的金融领域专家，先后曾担任过国有五大行之一的银行行长，大型金融集团的董事兼CEO，大型信托投资公司的CEO，具有极其丰富的资本运作的成功经验。</p>\' WHERE ( id = 1269 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1927','修改信息成功','【后台用户：xiaojie】修改信息成功。','7','1423122175','2','2','UPDATE `tp_info` SET `pid`=42,`title`=\'吉林省恺丰典当有限公司\',`img`=\'/Uploads/201501/54c09be6e658c.jpg\',`imgs`=\'/Uploads/201411/5462c93a2e4b9.jpg\\r\\n/Uploads/201411/5462c93a4cd01.jpg\\r\\n/Uploads/201411/5462c93a63b37.jpg\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=124,`time`=1415760070,`keyword`=\'\',`description`=\'​在公司成立进程中，恺丰典当的经营规模从小到大，典当经营品类由少到多，典当融资能力由弱到强，在帮助市民消费理财、推动个人创业投资、解决民营企业资金急需等方面发挥了典当特有的积极作用。\',`reg_fund`=\'8000万元\',`company_create_time`=\'2012年\',`join_time`=\'2014年3月\',`company_url`=\'\',`company_tel`=\'\',`company_address`=\'吉林省长春市朝阳区繁荣路5号\',`content`=\'<p>恺丰典当的经营规模从小到大，典当经营品类由少到多，典当融资能力由弱到强，在帮助市民消费理财、推动个人创业投资、解决民营企业资金急需等方面发挥了典当特有的积极作用。</p><p>在公司经营过程中，恺丰典当从练好内功、提高自身、发展优势上下功夫，通过优质的服务、完善的经营、通畅的运作，高效的决策，不断完善提升企业素质。</p><p><br/></p>\' WHERE ( id = 736 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1928','修改信息成功','【后台用户：xiaojie】修改信息成功。','7','1423122269','2','2','UPDATE `tp_info` SET `pid`=42,`title`=\'长春市鼎浩小额贷款公司\',`img`=\'/Uploads/201501/54c09c031489d.jpg\',`imgs`=\'/Uploads/201411/5460560a321c2.jpg\\r\\n/Uploads/201411/5460560a63b37.jpg\\r\\n/Uploads/201411/5460560a7a96d.jpg\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=120,`time`=1415599234,`keyword`=\'\',`description`=\'长春市鼎浩小额贷款有限公司于2014年1月15日在长春工商局登记注册，鼎浩小额贷款公司有最好的产品和专业的销售和技术团队，在公司发展壮大的1年里，他们为客户提供最好的产品、良好的技术支持、健全的售后服务，长春市鼎浩小额贷款有限公司是长春其他金融业行业知名企业。\',`reg_fund`=\'6700万元\',`company_create_time`=\'2014年1月\',`join_time`=\'2014年7月\',`company_url`=\'\',`company_tel`=\'0431--81858335\',`company_address`=\'长春市南关区亚泰大街5333号\',`content`=\'<p>长春市鼎浩小额贷款有限公司于2014年1月15日在长春工商局登记注册，我公司的办公地址位于中国第一汽车和“新中国电影的摇篮--长春，长春市南关区亚泰大街5333号。</p><p>我们有最好的产品和专业的销售和技术团队，在公司发展壮大的1年里，我们为客户提供最好的产品、良好的技术支持、健全的售后服务，长春市鼎浩小额贷款有限公司是长春其他金融业行业知名企业。</p><p>小额贷款公司作为地方金融组织，在引导民间资本和缓解中小企业融资难的过程中发挥着不可替代的作用，市县两级政府高度重视。市委、市政府高度重视小额贷款公司的发展。</p><p><br/></p>\' WHERE ( id = 120 )','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1929','修改信息成功','【后台用户：xiaojie】修改信息成功。','7','1423122749','2','2','UPDATE `tp_info` SET `pid`=42,`title`=\'吉林大华律师事务所\',`img`=\'/Uploads/201411/5460505163b37.jpg\',`imgs`=\'/Uploads/201502/54d32137c26d7.png\\r\\n/Uploads/201502/54d32137d1afb.png\\r\\n/Uploads/201502/54d3213826066.png\\r\\n/Uploads/201502/54d321383548a.png\\r\\n/Uploads/201502/54d3213866dff.png\',`url`=\'\',`tel`=\'\',`sort`=100,`click`=125,`time`=1415598099,`keyword`=\'\',`description`=\'由于大华铭仁律师事务所具有较高的发展起点和全新的法律服务体制，创业伊始就引起社会各界的广泛关注，呈现出强大的生命力。经过17年的不懈努力，本事务所已发展成为拥有三十名专职执业律师和业务人员，专业门类齐全，业务领域广阔的综合性律师事务所。\',`reg_fund`=\'中国四大律师事务所之一\',`company_create_time`=\'1994年7月8日\',`join_time`=\'2014年5月29日\',`company_url`=\'http://www.dhlawyer.com/\',`company_tel`=\'86-431-82716611\',`company_address`=\'长春市人民大街1556号1-2楼\',`content`=\'<p>大华铭仁律师事务所具有较高的发展起点和全新的法律服务体制，创业伊始就引起社会各界的广泛关注，呈现出强大的生命力。经过17年的不懈努力，本事务所已发展成为拥有三十名专职执业律师和业务人员，专业门类齐全，业务领域广阔的综合性律师事务所。大华铭仁律师把依法维护客户的合法权益，促进司法公正和实现社会正义作为自己的光荣使命，把“客户至上、勤勉尽责、求实守信、优质高效”确定为律师工作的基本准则。</p><p>伴随着经济全球化和中国改革开放的不断深入，以及市场经济的推进，来自于国内外不同层面的客户的法律服务需求也日趋复杂化和多样化。从满足客户的法律服务需求和市场需要出发，本事务所确立了以人为本的经营理念，在吸纳各类优秀人才的同时，大力培养人才。一批高素质、专业化、复合型的法律人才在广阔的法律服务领域正发挥着重要作用。</p><p><br/></p>\' WHERE ( id = 119 )','0');
