INSERT INTO `%DB_PREFIX%msg_box` VALUES ('1938','修改项目成功','【后台用户：admin】修改【项目信息：油箱公司流动资金周转】成功。','1','1423127080','2','2','UPDATE `tp_loan` SET `class_id`=0,`loan_title`=\'油箱公司流动资金周转\',`img`=\'\',`loan_money`=1000000,`min_loan_money`=100,`max_loan_money`=500000,`loan_rate`=12,`loan_add_rate`=0,`loan_deadline`=6,`loan_deadline_type`=0,`end_time`=30,`repayment_mode`=\'利随本清\',`guarantee_mode`=\'抵押物担保\',`guarantee_purview`=\'本金及利息\',`loan_purpose`=\'短期周转\',`pawn`=\'设备\',`production_value`=\'500万\',`payment_source`=\'经营收入\',`repayment_ability`=\'优\',`loan_description`=\'该公司技术力量雄厚，有独立设计开发各种汽车燃油箱的能力，设备精良，可为用户生产各种规格型号产品，制造处于国内先进水平。现主要为国内外著名汽车公司等配套生产设计制造各种类型的汽车燃油箱。公司在通过了ISO9002质量体系认证,QS-9000质量体系认证的,CCC中国国家强制性产品认证,具有完善的质量管理程序。<br/>本息保障<br/>1、由担保机构提供不可撤销的连带责任担保，严格把控项目风险，对本息100%保障。<br/>2、担保机构按规定计提30%的担保费作为风险保障金，由金投资与担保公司共同监管。<br/>3、借款企业向担保机构提供应收账款质押作为反担保措施。<br/>4、借款企业向担保机构提供提供100%股权质押。\',`company_name`=\'\',`company_info`=\'该公司技术力量雄厚，有独立设计开发各种汽车燃油箱的能力，设备精良，可为用户生产各种规格型号产品，制造处于国内先进水平。现主要为国内外著名汽车公司等配套生产设计制造各种类型的汽车燃油箱。公司在通过了ISO9002质量体系认证,QS-9000质量体系认证的,CCC中国国家强制性产品认证,具有完善的质量管理程序。\',`business_scope`=\'\',`business_state`=\'\',`guarantee_institution`=\'\',`guarantee_content`=\'\',`pawn_info`=\'\',`risk_control`=\'\',`guarantee_idea`=\'\',`company_material`=\'\',`pawn_action`=\'\',`contract_agreement`=\'/Uploads/201410/544f38478b501.jpg\\r\\n/Uploads/201410/544f3847a2337.jpg\\r\\n/Uploads/201410/544f38496159e.jpg\',`field_visit`=\'/Uploads/201410/544f3850c0b7f.jpg\\r\\n/Uploads/201410/544f3850d3cac.jpg\\r\\n/Uploads/201410/544f385120805.jpg\\r\\n/Uploads/201410/544f3854a2337.jpg\',`guarantee_title`=\'华兴泰丰担保公司\',`guarantee_measure`=\'\',`loan_order`=10,`deal_status`=4,`start_time`=1414425600,`bad_time`=0,`bad_msg`=\'\',`success_time`=0,`repay_confirm_time`=1416844800,`is_effect`=1,`recommend`=0,`update_time`=1423127080,`update_by`=\'admin\',`expire_time`=1432483200 WHERE ( id=10 )','0');
