INSERT INTO `%DB_PREFIX%msg_box` VALUES ('12868','添加项目成功','【后台用户：cuikai】添加【项目信息:汽车零部件公司流动资金周转】成功。','11','1423725624','2','2','INSERT INTO `tp_loan` (`class_id`,`loan_title`,`img`,`loan_money`,`min_loan_money`,`max_loan_money`,`loan_rate`,`loan_add_rate`,`loan_deadline`,`loan_deadline_type`,`end_time`,`repayment_mode`,`guarantee_mode`,`guarantee_purview`,`loan_purpose`,`pawn`,`production_value`,`payment_source`,`repayment_ability`,`loan_description`,`company_name`,`company_info`,`business_scope`,`business_state`,`guarantee_institution`,`guarantee_content`,`pawn_info`,`risk_control`,`guarantee_idea`,`company_material`,`pawn_action`,`contract_agreement`,`field_visit`,`guarantee_title`,`guarantee_measure`,`loan_order`,`deal_status`,`start_time`,`bad_time`,`bad_msg`,`success_time`,`repay_confirm_time`,`is_effect`,`recommend`,`create_time`,`update_by`) VALUES (0,\'汽车零部件公司流动资金周转\',\'/Uploads/201502/54dc52ec26066.jpg\',1000000,50,0,9,2,3,0,2,\'到期还本付息\',\'\',\'\',\'流动资金周转\',\'房产\',\'2000万\',\'1经营应收账款\\r\\n2企业自身申请银行贷款\',\'优\',\'借款企业为本地汽车零部件生产企业，为一汽集团一级配套供应商，对主机厂直接供货。由于帐期为N+4，在保证一汽供货的条件下企业又同时签署青岛一家汽车企业主机厂的供货合同，所以需要短期周转资金购置原材料以备扩大生产经营所需。\',\'\',\'企业注册资金为1700万元，在2014年度企业实际产值为2000万元，企业占地14000平米土地、4000余米厂房车间，价值上百万/台的大型数控机床8台，冲压、锻造等通用设备共20余台。\',\'汽车零部件加工制造\',\'由于企业生产的产品可实现运输配送，除为一汽配套外企业持续开发其他销售渠道，目前企业部分产品已经出口远销至俄罗斯，部分产品销售至青岛另一家国有大型汽车制造厂，供货上游企业稳定是本行业的特点。\',\'\',\'\',\'\',\'\',\'\',\'\',\'/Uploads/201502/54dc5410c63e0.jpg\\r\\n/Uploads/201502/54dc541190d62.jpg\',\'\',\'\',\'\',\'\',10,1,1423965635,0,\'\',0,0,1,0,1423725624,\'cuikai\')','0');
INSERT INTO `%DB_PREFIX%msg_box` VALUES ('12869','修改项目成功','【后台用户：cuikai】修改【项目信息：商贸公司资金周转】成功。','11','1423725658','2','2','UPDATE `tp_loan` SET `class_id`=0,`loan_title`=\'商贸公司资金周转\',`img`=\'/Uploads/201502/54d076c831781.jpg\',`loan_money`=500000,`min_loan_money`=50,`max_loan_money`=500000,`loan_rate`=8,`loan_add_rate`=8.8,`loan_deadline`=1,`loan_deadline_type`=0,`end_time`=3,`repayment_mode`=\'利随本清\',`guarantee_mode`=\'担保公司\',`guarantee_purview`=\'全额担保\',`loan_purpose`=\'工程投入\',`pawn`=\'房产\',`production_value`=\'10000万\',`payment_source`=\'经营收入\',`repayment_ability`=\'优\',`loan_description`=\'借款企业为长春本地家居商场，展厅面积9000+平米，由于预定五月一日开业，目前急需装修款进行内部装修打造体验店，同时由于具备双证可在银行申请直贷，但由于银行审批流程及抵押手续等原因在春节之前不能放款，为应对短期资金真空并赶工期装修故申请借款。\',`company_name`=\'\',`company_info`=\'借款企业为长春本地家居商场，展厅面积9000+平米，法人名下其他关联企业为：两座具有20余年装饰材料生产制造经验的大型工厂，其名下制造厂年产值一亿元左右，本项目位于长春最繁华商圈之一，日均客流10万人，目前土地证房产证俱全，是继长春太X城、红XX凯龙、欧X卖场之后又一专业销售家居中高端品牌商场，由于多年生产加工装饰材料，在全球拥有极其广泛的优质货源渠道，本企业采用O2O体验店结合方式注重成套家居体验，采用目前东北独家的线上销售线下交货模式经营，业务范围未来可迅速扩展整个吉林省。\',`business_scope`=\'装饰材料生产制造，家居产品销售\',`business_state`=\'未来本企业除整体O2O销售模式外，在线下实体店采用自身经营+销售展位经营权形式，由其运用自身优质资源为购买展位的商户提供货源，目前每米暂定销售价格为2万/平米/15年，可回租可在期满溢价赎回。如在银行贷款失败不能短期履行还款合约的情况下，本企业的展位销售可作为强有力的还款来源。同时为提高违约成本和增加制约手段，由其名下其他关联企业（包括工厂、房地产开发公司）签署无限连带责任保证，做到万无一失。\',`guarantee_institution`=\'\',`guarantee_content`=\'\',`pawn_info`=\'抵押物为企业名下部分房产，以成本价抵押，总价约为100万左右\',`risk_control`=\'实物资产抵押\',`guarantee_idea`=\'\',`company_material`=\'\',`pawn_action`=\'/Uploads/201502/54d076c80f230.jpg\\r\\n/Uploads/201502/54d076c866dff.jpg\\r\\n/Uploads/201502/54dc465cf404c.jpg\',`contract_agreement`=\'\',`field_visit`=\'\',`guarantee_title`=\'\',`guarantee_measure`=\'\',`loan_order`=10,`deal_status`=4,`start_time`=1423533600,`bad_time`=0,`bad_msg`=\'\',`success_time`=1423572533,`repay_confirm_time`=1423623617,`is_effect`=1,`recommend`=0,`update_time`=1423725658,`update_by`=\'cuikai\',`expire_time`=1426042817 WHERE ( id=17 )','0');
