INSERT INTO `%DB_PREFIX%msg_box` VALUES ('12871','修改项目成功','【后台用户：cuikai】修改【项目信息：房地产公司流动资金周转】成功。','11','1423725686','2','2','UPDATE `tp_loan` SET `class_id`=0,`loan_title`=\'房地产公司流动资金周转\',`img`=\'/Uploads/201501/54b46f11a49ca.jpg\',`loan_money`=500000,`min_loan_money`=50,`max_loan_money`=100000,`loan_rate`=8,`loan_add_rate`=8.8,`loan_deadline`=15,`loan_deadline_type`=1,`end_time`=3,`repayment_mode`=\'利随本清\',`guarantee_mode`=\'足值抵押\',`guarantee_purview`=\'本金及利息\',`loan_purpose`=\'流动资金周转\',`pawn`=\'房产\',`production_value`=\'1000万\',`payment_source`=\'应收账款\',`repayment_ability`=\'优\',`loan_description`=\'借款企业为长春本地房地产企业，前身为成立于1994年的长春本地SP\\r\\n商，企业转型后在大连成立集团公司，致力于教育、软件开发、房地产等多个领域。借款企业法人名下其他关联企业也在长春本地有很大的影响力。在建楼盘位于长\\r\\n春二环以内繁华路段，位置优越，交通便利，前期总共投入六亿自有资金，目前楼盘已经全部封顶并获得了销售许可等相关五证，具备开盘销售资格，楼盘建筑面积\\r\\n为十二万平米，包括商服门市、住宅、写字间。周边地段均价为8000/平米，目前开盘团购价为6000+/平米。冬季为楼盘销售淡季，但已\\r\\n经封顶需要结算工程款，故需求资金结算工程款。借款企业在春季进入房产销售旺季后，用销售楼盘所得资金作为还款来源，以保守最低均价6500/平米销售计\\r\\n算，企业去掉投资成本最低可赚取利润1.8亿元。以单个住宅销售计算，百平米住宅销售65万元，本次借款金额为50万。即售出一套房产就可进行偿还借款。\\r\\n所以还款来源极其充分。在房地产下行的今天，地产企业是谨入行业，但本次借款以略微高于工程造价的成本价作为折算率，即4500/平米，抵\\r\\n押建筑面积100平米的单元两套，共计200平米房产，抵押物估值达90万元，远高于借款金额。同时以借款企业法人名下的一所占地面积15万平米的大型计\\r\\n算机信息培训学校，年招生五千余人，作为本次贷款的无限连带责任担保方。增强制约手段、提高借款企业违约成本，做到万无一失。\',`company_name`=\'\',`company_info`=\'\',`business_scope`=\'\',`business_state`=\'\',`guarantee_institution`=\'\',`guarantee_content`=\'\',`pawn_info`=\'\',`risk_control`=\'\',`guarantee_idea`=\'\',`company_material`=\'\',`pawn_action`=\'\',`contract_agreement`=\'\',`field_visit`=\'/Uploads/201501/54b46f116793a.jpg\\r\\n/Uploads/201501/54b46f1186182.jpg\\r\\n/Uploads/201501/54b46f11a49ca.jpg\\r\\n/Uploads/201501/54b46f12453e9.jpg\',`guarantee_title`=\'\',`guarantee_measure`=\'\',`loan_order`=9,`start_time`=1421632800,`bad_time`=0,`bad_msg`=\'\',`success_time`=1421892072,`repay_confirm_time`=1421892000,`is_effect`=1,`recommend`=0,`update_time`=1423725686,`update_by`=\'cuikai\',`expire_time`=1423188000 WHERE ( id=16 )','0');
