INSERT INTO `%DB_PREFIX%msg_box` VALUES ('27265','修改项目成功','【后台用户：cuikai】修改【项目信息：汽车用品企业流动资金周转】成功。','11','1427699150','2','2','UPDATE `tp_loan` SET `class_id`=0,`loan_title`=\'汽车用品企业流动资金周转\',`img`=\'\',`loan_money`=1500000,`min_loan_money`=100,`max_loan_money`=1000000,`loan_rate`=12,`loan_add_rate`=0,`loan_deadline`=4,`loan_deadline_type`=0,`end_time`=30,`repayment_mode`=\'利随本清\',`guarantee_mode`=\'抵押物担保\',`guarantee_purview`=\'本金及利息\',`loan_purpose`=\'流动资金周转\',`pawn`=\'厂房、土地\',`production_value`=\'1000万\',`payment_source`=\'应收账款、土地证办理下来后进行银行抵押贷款\',`repayment_ability`=\'优\',`loan_description`=\'基本信息：根据金投资网贷平台考证，该企业位于长春东部工业园区内，为一家汽车配套企业，其上游企业主机厂为吉林某轻型车厂，专业生产汽车座椅套及其他附属产品。<br/>\\r\\n贷款用途：由于位于工业园区内，厂房土地为以租代购形式获得，支付租金已经5年，剩余租金不多，<br/>\\r\\n而经过5年的房产土地增值本企业的房产已经大于当初购买价格，故企业主欲一次结清买断房产土地，再办理房产土地证进行银行抵押贷款，用于流动资金周转。\\r\\n足值抵押：企业名下的厂房土地\\r\\n还款来源：应收账款、土地证办理下来后进行银行抵押贷款。\',`company_name`=\'\',`company_info`=\'根据金投资网贷平台考证，该借款企业是一家以生产汽车内饰、汽车外饰等产品为主营业务的公司。是国内著名汽车整车企业的核心供应商，也是国内外多家知名整车企业的战略合作伙伴。名下自有房产1700余米，年产值1000万元。公司自成立以来，秉承质优价廉，服务至上，以新颖的款式、公道的价格、一流的质量和周到的服务，<br/>\\r\\n赢得了业内人士的一致好评。现产品已遍布东北各大专业市场。\\r\\n公司紧跟中国汽车行业发展步伐，凭借科学稳健的经营管理，不断整合资源，优化产品结构，现已形成遍布中国5省7市的产业格局。\\r\\n公司现有员工300余人，多年的经营实践中，企业坚持“以人为本”的管理理念，最大限度为员工创造岗位成才的舞台。培养锻炼具有高级专业技术职称人员组成的高技术、管理型专家团队。企业具备强大的技术优势与项目管理能力，支撑起在全国同行业居于领先地位的产品中心、运控中心、验证中心。公司倡导技术创新与持续改进相结合，公司始终秉承“超越客户期望”的质量方针，建立并推行FOS管理体系，产品质量不断提高。 \\r\\n面向未来，公司将依托厚重积累，强化自主创新，不断为用户制造满足需要、安全环保、品质卓越、服务至诚的产品。全力打造国内领先、国际知名的汽车零部件企业。 \',`business_scope`=\'汽车内饰精品、汽车脚垫、汽车座套等产品\',`business_state`=\'年产值1000万元，借款公司是一家综合性汽车用品企业，产品主要汽车坐垫套。<br/>\\r\\n公司自成立以来，秉承质优价廉，服务至上，以新颖的款式、公道的价格、一流的质量和周到的服务，<br/>\\r\\n赢得了业内人士的一致好评。现产品已遍布东北各大专业市场。\',`guarantee_institution`=\'\',`guarantee_content`=\'\',`pawn_info`=\'企业名下的厂房土地\',`risk_control`=\'1、足值抵押。\\r\\n2、全额本息担保。\\r\\n3、全程法律服务\',`guarantee_idea`=\'\',`company_material`=\'/Uploads/201503/55028519b3a83.png\',`pawn_action`=\'\',`contract_agreement`=\'/Uploads/201410/544f37d3d79b5.png\',`field_visit`=\'\',`guarantee_title`=\'\',`guarantee_measure`=\'1、足值抵押、2、全额本息担保\',`loan_order`=10,`start_time`=1414425600,`bad_time`=0,`bad_msg`=\'\',`success_time`=0,`repay_confirm_time`=1416844800,`is_effect`=1,`recommend`=0,`update_time`=1427699150,`update_by`=\'cuikai\',`expire_time`=1427212800 WHERE ( id=7 )','0');
