INSERT INTO `%DB_PREFIX%msg_box` VALUES ('29598','添加信息成功','【后台用户：sunyan】添加信息成功。','6','1427787896','2','2','INSERT INTO `tp_info` (`pid`,`title`,`img`,`imgs`,`url`,`tel`,`sort`,`click`,`time`,`keyword`,`description`,`reg_fund`,`company_create_time`,`join_time`,`company_url`,`company_tel`,`company_address`,`content`) VALUES (23,\'怎样正确培养孩子的个人投资理财观念呢？\',\'/Uploads/201503/551a503ff1d24.jpg\',\'\',\'\',\'\',100,100,1427787819,\'个人投资理财\',\'​我们常说好习惯的养成要从小开始，但是提到理财很多家长却不愿在孩子面前提到，个人投资理财的好习惯和生活中的习惯是一样的，都要从小做起，那么怎样正确培养孩子的个人投资理财观念呢？下面我们从两方面入手了解一下吧。\',\'\',\'\',\'\',\'\',\'\',\'\',\'<p>我们常说好习惯的养成要从小开始，但是提到理财很多家长却不愿在孩子面前提到，个人投资理财的好习惯和生活中的习惯是一样的，都要从小做起，那么怎样正确培养孩子的个人投资理财观念呢？下面我们从两方面入手了解一下吧。<br/></p><p><br/></p><p><strong>一方面我们从父母着手：不要带给孩子错误的理财示范：</strong></p><p><br/></p><p>父母在生活中的一些错误理财示范也不利于子女的理财观念。例如许多家庭对于孩子的需求总是有求必应，导致孩子不珍惜金钱也不懂得节制；而父母本身在花钱的时候也没有以身作则，教育孩子不要乱花钱的同时自己却花钱没有计划；时常为了钱争吵很容易让孩子产生“金钱是不好的东西”的观念；一些父母会答应孩子考试满分或达到某一目标时给予奖励，但如果没有兑现言行则容易给孩子带来不好的影响；而一些父母为了显示关爱孩子，容易带孩子去参加一些费用很高的游乐项目，不想让孩子有太多的压力，但是这样的想法真的是对的吗？怎样正确培养孩子的个人投资理财观念呢？</p><p><br/></p><p><strong>另一方面应该怎样改进个人投资理财呢：</strong></p><p><br/></p><p>作为父母应该从小培养孩子的个人投资理财观念，让孩子知道父母的钱也是来之不易的，可以从三个方面让孩子建立其健康的财商观念。怎样正确培养孩子的个人投资理财观念呢？一是树立正确理财观，培养孩子良好的理财习惯，父母可从日常生活教育着手，进行孩子的理财教育及观念培养；二是小钱成大钱，每个父母都会给孩子零花钱，将这些零散资金学会系统运用是教会孩子理财的重要一步，建议父母们可以教孩子怎样做好强制储蓄，学会攒钱，这样孩子才能逐渐建立正确的理财观。</p><p><br/></p><p>个人投资理财 <a href=\\\"http://www.p2p222.com\\\" target=\\\"_blank\\\" title=\\\"金投资\\\"><strong>【金投资】</strong></a> http://www.p2p222.com/</p><p><br/></p>\')','0');
