INSERT INTO `%DB_PREFIX%msg_box` VALUES ('31240','修改项目成功','【后台用户：cuikai】修改【项目信息：装饰材料企业流动资金周转】成功。','11','1427898745','2','2','UPDATE `tp_loan` SET `class_id`=0,`loan_title`=\'装饰材料企业流动资金周转\',`img`=\'\',`loan_money`=2400000,`min_loan_money`=100,`max_loan_money`=1000000,`loan_rate`=10,`loan_add_rate`=0,`loan_deadline`=4,`loan_deadline_type`=0,`end_time`=30,`repayment_mode`=\'利随本清\',`guarantee_mode`=\'抵押物担保\',`guarantee_purview`=\'本金及利息\',`loan_purpose`=\'短期周转\',`pawn`=\'主要生产的产品\',`production_value`=\'2亿\',`payment_source`=\'应收账款\',`repayment_ability`=\'优\',`loan_description`=\'基础信息：根据金投资网贷平台考证，该借款企业为生产地板、木材及装饰材料为主的企业，是吉林某大型国有地板厂的配套厂家。\\r\\n借款用途：借款企业本地贷款主要用做购买生产设备，以及现有设备的升级与改造，<br/>\\r\\n足值抵押物：由于其生产地板成品，库存充足，而地板成品又具备高流动性，故与其签署远低于市场价格的购销合同，<br/>\\r\\n如出现违约及不良可直接销售其成品地板作为偿还来源。\\r\\n连带担保：与借款企业的其他关联企业签署无限连带责任书作为充足担保来源。\\r\\n还款来源：应收账款\',`company_name`=\'\',`company_info`=\'根据金投资网贷平台考证，该借款企业是一家快速崛起的国际绿色装饰材料生产企业。占地6.4万平，建造了八栋国际标准化厂房，实现集约集聚发展，致力于强化地板的产品研发、生产制造与销售服务等的一体化运作，拥有一大批国际专业科研、生产、营销和服务人才。拥有10台2800吨低压短周期热压机和多条先进的德国豪迈生产线，年产量500万平方米，现为北方最大的强化地板生产加工基地。借款企业为生产地板、木材及装饰材料为主的企业，是吉林某大型国有地板厂的配套厂家，<br/>\\r\\n专门为此大型配套厂家供应地板成品，账期稳定、订单充足，2014年产值一亿元。<br/>该企业从创业之初就秉承“以诚信树立品牌，以技术创造价值，以服务满意顾客”的理念，<br/>\\r\\n以“品质第一，顾客至上”为宗旨，从事开发、制造和销售装饰材料。公司目前拥有多台高精密生产设备和具有一批较强专业水平的技术人才。在此行业中率先提出“竭诚服务、高效反馈、迅捷响应、 最佳效益”的服务理念，从而和客户之间能够建立供需双方的绿色通道，达成“双赢”的局面。\\r\\n\',`business_scope`=\'绿色装饰材料生产\',`business_state`=\'年产量500万平方米，账期稳定、订单充足，2014年产值一亿元。\',`guarantee_institution`=\'\',`guarantee_content`=\'\',`pawn_info`=\'1、由于其生产地板成品，库存充足，而地板成品又具备高流动性，故与其签署远低于市场价格的购销合同，<br/>\\r\\n如出现违约及不良可直接销售其成品地板作为偿还来源。\\r\\n2、与借款企业的其他关联企业签署无限连带责任书作为充足担保来源。\',`risk_control`=\'1、足值抵押。\\r\\n2、关联企业无限连带担保\\r\\n3、担保机构全额本息担保\',`guarantee_idea`=\'\',`company_material`=\'\',`pawn_action`=\'/Uploads/201503/5502862a85e17.png\\r\\n/Uploads/201503/5502862ab778c.png\\r\\n/Uploads/201503/5502862abf19e.png\\r\\n/Uploads/201503/5502862b50799.png\\r\\n/Uploads/201503/5502862b5beb4.png\',`contract_agreement`=\'\',`field_visit`=\'\',`guarantee_title`=\'\',`guarantee_measure`=\'1、足值抵押。 2、关联企业无限连带担保 3、担保机构全额本息担保\',`loan_order`=3,`start_time`=1414425600,`bad_time`=0,`bad_msg`=\'\',`success_time`=0,`repay_confirm_time`=1416844800,`is_effect`=1,`recommend`=0,`update_time`=1427898745,`update_by`=\'cuikai\',`expire_time`=1427212800 WHERE ( id=5 )','0');
