INSERT INTO `%DB_PREFIX%msg_box` VALUES ('31244','修改项目成功','【后台用户：cuikai】修改【项目信息：表面技术公司资金周转】成功。','11','1427898763','2','2','UPDATE `tp_loan` SET `class_id`=0,`loan_title`=\'表面技术公司资金周转\',`img`=\'/Uploads/201410/544f380b9a925.jpg\',`loan_money`=2400000,`min_loan_money`=100,`max_loan_money`=1000000,`loan_rate`=10,`loan_add_rate`=0,`loan_deadline`=3,`loan_deadline_type`=0,`end_time`=30,`repayment_mode`=\'利随本清\',`guarantee_mode`=\'抵押担保\',`guarantee_purview`=\'本金及利息\',`loan_purpose`=\'短期周转\',`pawn`=\'设备\',`production_value`=\'4000万\',`payment_source`=\'经营收入，应收账款\',`repayment_ability`=\'优\',`loan_description`=\'基础信息：根据金投资网贷平台考证，该借款企业位于长春南部，厂房面积为17000余米，上游企业为国内赫赫有名的多家大型汽车集团公司，<br/>\\r\\n去年产值为5800万元，员工为100余人，24小时人停机器不停加班生产。\\r\\n借款用途：由于订单需求突飞猛进，急需资金进行周转扩大生产加装第4条生产线。\\r\\n足值抵押物：借款企业的机械设备生产线，为增加违约成本又加上部分应收帐款作为质押。由于企业用款急迫，企业的厂房可以在银行作为抵押置换本次借款。\\r\\n还款来源：经营收入、应收账款\',`company_name`=\'\',`company_info`=\'根据金投资网贷平台考证，该借款企业厂区占地面积23217㎡，建筑面积 16138㎡，现有员工160人。公司现已成为集汽车零部件面漆,阴极电泳涂装，粉末涂装，喷砂处理等多种业务于一身的综合性表面技术公司。\\r\\n公司现已成为汽车零部件面漆,阴极电泳涂装，粉末静电喷涂等涂装业务的综合性表面技术公司，主导产品来源于国内知名汽车制造企业等。知名客户均为国内知名汽车企业。\\r\\n公司现已建成3条国内先进的自动阴极电泳生产线和粉末静电喷涂设备，具有较强的生产能力和过程开发能力。拥有先进的检测和试验设备，采购国际知名企业的原材料，具有较强的质量保证能力。\\r\\n公司已通过了ISO/TS16949（2009）质量管理体系认证，积极引入创新思想，采用具有领先水平的涂装理念和方法。\\r\\n通过先进的工艺，良好的设备，精心的制造，严格的管理，为顾客提供高品质产品，确保企业可持续发展。\',`business_scope`=\'汽车零部件面漆,阴极电泳涂装，粉末涂装，喷砂处理等综合性表面技术\',`business_state`=\'2014年总产值达8200万元，2015年预计总产值将达到一亿元。\',`guarantee_institution`=\'\',`guarantee_content`=\'\',`pawn_info`=\'抵押物为借款企业的机械设备生产线，为增加违约成本又加上部分应收帐款作为质押。由于企业用款急迫，企业的厂房可以在银行作为抵押置换本次借款。\',`risk_control`=\'1、足值抵押、\\r\\n2、担保机构全额本息担保、\\r\\n3、关联企业无限连带担保\',`guarantee_idea`=\'\',`company_material`=\'\',`pawn_action`=\'/Uploads/201503/550283e4afd7a.png\\r\\n/Uploads/201503/550283e50fa00.png\\r\\n/Uploads/201503/550283e513709.png\',`contract_agreement`=\'/Uploads/201410/544f3800652a7.jpg\\r\\n/Uploads/201410/544f3800783d4.jpg\\r\\n/Uploads/201410/544f38033b344.jpg\',`field_visit`=\'\',`guarantee_title`=\'\',`guarantee_measure`=\'1、足值抵押、2、担保机构全额本息担保、3、关联企业无限连带担保\',`loan_order`=3,`start_time`=1414425600,`bad_time`=0,`bad_msg`=\'\',`success_time`=0,`repay_confirm_time`=1416844800,`is_effect`=1,`recommend`=0,`update_time`=1427898763,`update_by`=\'cuikai\',`expire_time`=1424793600 WHERE ( id=8 )','0');
