INSERT INTO `%DB_PREFIX%msg_box` VALUES ('39007','添加项目成功','【后台用户：cuikai】添加【项目信息:汽车零部件公司流动资金周转三期】成功。','11','1428381880','2','2','INSERT INTO `tp_loan` (`class_id`,`loan_title`,`img`,`loan_money`,`min_loan_money`,`max_loan_money`,`loan_rate`,`loan_add_rate`,`loan_deadline`,`loan_deadline_type`,`end_time`,`repayment_mode`,`guarantee_mode`,`guarantee_purview`,`loan_purpose`,`pawn`,`production_value`,`payment_source`,`repayment_ability`,`loan_description`,`company_name`,`company_info`,`business_scope`,`business_state`,`guarantee_institution`,`guarantee_content`,`pawn_info`,`risk_control`,`guarantee_idea`,`company_material`,`pawn_action`,`contract_agreement`,`field_visit`,`guarantee_title`,`guarantee_measure`,`loan_order`,`deal_status`,`start_time`,`bad_time`,`bad_msg`,`success_time`,`repay_confirm_time`,`is_effect`,`recommend`,`create_time`,`update_by`) VALUES (0,\'汽车零部件公司流动资金周转三期\',\'/Uploads/201503/5516712d96589.jpg\',300000,50,300000,9,3,45,1,3,\'利随本清\',\'担保公司担保\',\'全额本息担保\',\'资金周转\',\'应收账款\',\'3000万\',\'经营收入\',\'优\',\'因近期订单较多，急需资金扩大生产，总借款额为150万，此次为第三期，数额为30万\',\'\',\'根据金投资网贷平台考证，长春XX汽车零部件有限公司是专业生产工位器具的公司，即企业在生产现场（一般指生产线）或仓库中用以存放生产对象或工具的各种装置。是用于盛装各种零部件、原材料等，满足现生产需要，方便生产工人操作，所使用的辅助性器具，是生产过程中每一个环节所不能缺少的。汽车装配线常用的工位器具主要包括：工作台、零件存放架、分装台（架）、料箱、料斗、集配箱、运转小车等。公司经营多年，积累了丰富的技术经验和客户经验，主要为一汽大众、采埃孚、富奥汽车、一汽轿车、一汽解放等大型国有和外资汽车生产企业提供工位器具的生产。年销售额3000余万元。\',\'专业生产工位器具的公司，即企业在生产现场（一般指生产线）或仓库中用以存放生产对象或工具的各种装置\',\'\',\'吉林北方汽车产业担保有限公司\',\'与金投资网贷平台合作的吉林北方汽车产业担保有限公司，经省、市中小企业局批准于2008年11月正式成立，是长春汽车产业开发区的融资平台。公司注册资本1亿元，经营范围是为中小企业提供贷款担保以及融资服务咨询。总经理李启祥曾任一汽集团公司副总经理兼总经济师。北方担保公司将业务定位于汽车产业链，为给一汽及其下属单位配套的中小企业解决资金不足，融资难的问题。 北方担保与交通银行、吉林银行、民生银行、招商银行、信合等多家金融机构建立了合作关系。\',\'\',\'\',\'\',\'\',\'/Uploads/201503/5516712d9a292.jpg\\r\\n/Uploads/201503/5516c93e87028.jpg\',\'/Uploads/201503/5516cc348331f.jpg\\r\\n/Uploads/201503/5516cc34cbaca.jpg\\r\\n/Uploads/201503/5516cc34d34dc.jpg\',\'\',\'\',\'\',10,1,1428386400,0,\'\',0,0,1,0,1428381880,\'cuikai\')','0');
