INSERT INTO `%DB_PREFIX%msg_box` VALUES ('40419','修改项目成功','【后台用户：cuikai】修改【项目信息：汽车零部件公司流动资金周转三期】成功。','11','1428458747','2','2','UPDATE `tp_loan` SET `class_id`=0,`loan_title`=\'汽车零部件公司流动资金周转三期\',`recommend`=1,`img`=\'/Uploads/201503/5516712d96589.jpg\',`loan_money`=300000,`min_loan_money`=50,`max_loan_money`=300000,`loan_rate`=9,`loan_add_rate`=3,`loan_deadline`=45,`loan_deadline_type`=1,`end_time`=3,`repayment_mode`=\'利随本清\',`guarantee_mode`=\'担保公司担保\',`guarantee_purview`=\'全额本息担保\',`loan_purpose`=\'资金周转\',`pawn`=\'应收账款\',`production_value`=\'3000万\',`payment_source`=\'经营收入\',`repayment_ability`=\'优\',`loan_description`=\'因近期订单较多，急需资金扩大生产，总借款额为150万，此次为第三期，数额为30万\',`company_name`=\'\',`company_info`=\'根据金投资网贷平台考证，长春XX汽车零部件有限公司是专业生产工位器具的公司，即企业在生产现场（一般指生产线）或仓库中用以存放生产对象或工具的各种装置。是用于盛装各种零部件、原材料等，满足现生产需要，方便生产工人操作，所使用的辅助性器具，是生产过程中每一个环节所不能缺少的。汽车装配线常用的工位器具主要包括：工作台、零件存放架、分装台（架）、料箱、料斗、集配箱、运转小车等。公司经营多年，积累了丰富的技术经验和客户经验，主要为一汽大众、采埃孚、富奥汽车、一汽轿车、一汽解放等大型国有和外资汽车生产企业提供工位器具的生产。年销售额3000余万元。\',`business_scope`=\'专业生产工位器具的公司，即企业在生产现场（一般指生产线）或仓库中用以存放生产对象或工具的各种装置\',`business_state`=\'\',`guarantee_institution`=\'吉林北方汽车产业担保有限公司\',`guarantee_content`=\'与金投资网贷平台合作的吉林北方汽车产业担保有限公司，经省、市中小企业局批准于2008年11月正式成立，是长春汽车产业开发区的融资平台。公司注册资本1亿元，经营范围是为中小企业提供贷款担保以及融资服务咨询。总经理李启祥曾任一汽集团公司副总经理兼总经济师。北方担保公司将业务定位于汽车产业链，为给一汽及其下属单位配套的中小企业解决资金不足，融资难的问题。 北方担保与交通银行、吉林银行、民生银行、招商银行、信合等多家金融机构建立了合作关系。\',`pawn_info`=\'\',`risk_control`=\'\',`guarantee_idea`=\'\',`company_material`=\'\',`pawn_action`=\'/Uploads/201503/5516712d9a292.jpg\\r\\n/Uploads/201503/5516c93e87028.jpg\',`contract_agreement`=\'/Uploads/201503/5516cc348331f.jpg\\r\\n/Uploads/201503/5516cc34cbaca.jpg\\r\\n/Uploads/201503/5516cc34d34dc.jpg\',`field_visit`=\'\',`guarantee_title`=\'\',`guarantee_measure`=\'\',`loan_order`=10,`deal_status`=4,`start_time`=1428386400,`bad_time`=0,`bad_msg`=\'\',`success_time`=1428413995,`repay_confirm_time`=1428458400,`is_effect`=1,`update_time`=1428458747,`update_by`=\'cuikai\',`expire_time`=1432346400 WHERE ( id=31 )','0');
